// src/context/ReservationsContext.js

import { createContext, useContext, useEffect, useMemo, useState } from "react";
import { msalInstance, loginRequest } from '../msalConfig';

// Contexto
const ReservationsCtx = createContext(null);

// Función auxiliar para reconstruir fechas (strings -> Date objects)
const reviveDates = (reservations) => {
    if (!reservations || !Array.isArray(reservations)) return [];

    return reservations.map(res => ({
        ...res,
        // Convertir las cadenas de tiempo ISO a objetos Date
        startTime: res.startTime ? new Date(res.startTime) : undefined,
        endTime: res.endTime ? new Date(res.endTime) : undefined,
        // Nota: Asegúrate de que r.room.id exista si lo usas en el contexto.
    }));
};

// Función auxiliar para extraer nombre de sala
const extractRoomName = (evt) => {
    // 1. Prioridad: Recurso en asistentes
    const roomAttendee = evt.attendees?.find(a => a.type === 'resource');
    if (roomAttendee) return roomAttendee.emailAddress?.name;

    // 2. Prioridad: Ubicación
    if (evt.location?.displayName && evt.location.displayName !== "Sin sala") {
        return evt.location.displayName;
    }
    return 'Sala no especificada';
};

// 1. DEFINICIÓN DE SALAS INICIALES (Por defecto)
const INITIAL_ROOMS = [
  { id: '1', nombre: 'Círculo de Sabios I', email: 'circulo.sabios1@fycotelecom.com', capacidad: 5, recursos: ['TV', 'Aire', 'HDMI'] },
  { id: '2', nombre: 'Círculo de Sabios II', email: 'circulo.sabios2@fycotelecom.com', capacidad: 5, recursos: ['TV', 'Aire', 'HDMI'] },
  { id: '3', nombre: 'Círculo de Sabios III', email: 'circulo.sabios3@fycotelecom.com', capacidad: 5, recursos: ['TV', 'Aire','HDMI'] },
  { id: '4', nombre: 'Consejo de la Tribu', email: 'consejo.tribu@fycotelecom.com', capacidad: 12, recursos: ['Pantalla', 'Aire'] },
  { id: '5', nombre: 'Plaza de Conexiones', email: 'plaza.conexiones@fycotelecom.com', capacidad: 6, recursos: ['Pantalla', 'Aire'] },
];

export const ReservationsProvider = ({ children }) => {
    const [reservations, setReservations] = useState([]);


    // Lógica de carga inicial de Outlook
const fetchReservations = async () => {
    // ... (Tu lógica para obtener reservas de Outlook) ...
    const outlookEvents = [/* ... datos recibidos ... */]; 

    // APLICA LA CONVERSIÓN AQUÍ
    const revivedEvents = reviveDates(outlookEvents); 
    setReservations(revivedEvents);
};
    // =========================================================================
    // VERIFICAR DISPONIBILIDAD (getSchedule)
    // =========================================================================
    const checkAvailability = async (roomEmail, dateString) => {
        const account = msalInstance.getActiveAccount();
        if (!account || !roomEmail) return [];

        try {
            const tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
            const token = tokenResponse.accessToken;

            // Definir rango del día completo (00:00 a 23:59) en hora local
            // Nota: Graph requiere ISO strings. Usaremos la fecha seleccionada.
            const startDateTime = `${dateString}T00:00:00`;
            const endDateTime = `${dateString}T23:59:59`;

            const payload = {
                schedules: [roomEmail],
                startTime: {
                    dateTime: startDateTime,
                    timeZone: "America/Bogota"
                },
                endTime: {
                    dateTime: endDateTime,
                    timeZone: "America/Bogota"
                },
                availabilityViewInterval: 15 // Intervalos de 15 min
            };

            const response = await fetch('https://graph.microsoft.com/v1.0/me/calendar/getSchedule', {
                method: 'POST',
                headers: { 
                    'Authorization': `Bearer ${token}`, 
                    'Content-Type': 'application/json',
                    'Prefer': 'outlook.timezone="America/Bogota"' 
                },
                body: JSON.stringify(payload)
            });

            if (!response.ok) return [];

            const data = await response.json();
            // La respuesta viene anidada en data.value[0].scheduleItems
            const schedule = data.value?.[0]?.scheduleItems || [];

            // Filtramos solo lo que esté ocupado (busy)
            return schedule.filter(item => item.status !== 'free').map(item => ({
                start: item.start.dateTime, // Ej: "2025-12-09T09:00:00"
                end: item.end.dateTime
            }));

        } catch (error) {
            console.error("Error verificando disponibilidad:", error);
            return [];
        }
    };

    // =========================================================================
    // NUEVO: GESTIÓN DE ESTADO DE SALAS (ROOMS)
    // =========================================================================

    // Inicializar estado de salas (Local Storage o Default)
    const [rooms, setRooms] = useState(() => {
        try {
            const saved = localStorage.getItem('app_rooms');
            return saved ? JSON.parse(saved) : INITIAL_ROOMS;
        } catch (e) {
            console.error("Error al cargar salas:", e);
            return INITIAL_ROOMS;
        }
    });

    // Guardar en Local Storage cada vez que cambien las salas
    useEffect(() => {
        localStorage.setItem('app_rooms', JSON.stringify(rooms));
    }, [rooms]);

    // Función: Agregar o Editar Sala
    const addOrUpdateRoom = (room) => {
        setRooms(prev => {
            const exists = prev.find(r => r.id === room.id);
            if (exists) {
                // Actualizar existente
                return prev.map(r => r.id === room.id ? room : r);
            } else {
                // Crear nueva (Generar ID si es 'NEW' o viene vacío)
                const newId = (room.id === 'NEW' || !room.id) ? Date.now().toString() : room.id;
                const newRoom = { ...room, id: newId };
                return [...prev, newRoom];
            }
        });
    };

    // Función: Eliminar Sala
    const deleteRoom = (roomId) => {
        setRooms(prev => prev.filter(r => r.id !== roomId));
    };


    // =========================================================================
    // 2. CARGA INICIAL DE DATOS (GET)
    // =========================================================================
    useEffect(() => {
        async function fetchReservations() {
            const account = msalInstance.getActiveAccount();
            if (!account) return; 

            try {
                const tokenResponse = await msalInstance.acquireTokenSilent({
                    ...loginRequest,
                    account: account
                });
                const token = tokenResponse.accessToken;

                const response = await fetch('https://graph.microsoft.com/v1.0/me/events?$select=subject,start,end,location,id,attendees&$top=50', {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Prefer': 'outlook.timezone="America/Bogota"'
                    }
                });

                if (!response.ok) throw new Error(`Error Graph: ${response.status}`);

                const graphData = await response.json();

                const mapped = (graphData.value || []).map(evt => ({
                    id: evt.id,
                    title: evt.subject,
                    startTime: evt.start.dateTime,
                    endTime: evt.end.dateTime,
                    room: { nombre: extractRoomName(evt) },
                    attendees: evt.attendees || [],
                    location: evt.location?.displayName || ''
                }));

                setReservations(reviveDates(mapped));

            } catch (error) {
                console.error("Error cargando reservas:", error);
            }
        }

        fetchReservations();
    }, []); 

    // =========================================================================
    // 3. CREAR O MODIFICAR (POST / PATCH)
    // =========================================================================
    const addOrUpdate = async (reservationPayload) => {
        const isModification = !!reservationPayload.id;
        const reservationId = reservationPayload.id;

        const account = msalInstance.getActiveAccount();
        if (!account) throw new Error("Sin sesión activa");

        let tokenResponse; 
        try {
            tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
        } catch {
            msalInstance.loginRedirect(loginRequest);
            return;
        }
        const token = tokenResponse.accessToken;

        const url = isModification 
            ? `https://graph.microsoft.com/v1.0/me/events/${reservationId}` 
            : 'https://graph.microsoft.com/v1.0/me/events';
        const method = isModification ? 'PATCH' : 'POST';

        const event = {
            subject: reservationPayload.title,
            start: { dateTime: reservationPayload.startTime, timeZone: "America/Bogota" },
            end: { dateTime: reservationPayload.endTime, timeZone: "America/Bogota" },
            location: { displayName: reservationPayload.room.nombre }, 
            attendees: [{
                emailAddress: { address: reservationPayload.roomEmail, name: reservationPayload.room.nombre },
                type: "resource"
            }]
        };

        const response = await fetch(url, {
            method: method,
            headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
            body: JSON.stringify(event)
        });

        if (!response.ok) {
            const err = await response.json();
            throw new Error(err.error?.message || "Error en la API");
        }

        const apiResponse = await response.json();

        setReservations(prev => {
            const newRes = {
                ...reservationPayload,
                id: apiResponse.id,
                startTime: new Date(apiResponse.start.dateTime),
                endTime: new Date(apiResponse.end.dateTime)
            };

            if (isModification) {
                return prev.map(r => r.id === reservationId ? newRes : r);
            } else {
                return [newRes, ...prev];
            }
        });
    };

    // =========================================================================
    // 4. ELIMINAR (DELETE)
    // =========================================================================
    const removeById = async (reservationId) => {
        const account = msalInstance.getActiveAccount();
        if (!account) return;

        const tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
        const token = tokenResponse.accessToken;

        const response = await fetch(`https://graph.microsoft.com/v1.0/me/events/${reservationId}`, {
            method: 'DELETE',
            headers: { 'Authorization': `Bearer ${token}` }
        });

        if (response.ok) {
            setReservations(prev => prev.filter(r => r.id !== reservationId));
        }
    };

    // Exponemos todo en el valor del contexto
    const value = useMemo(() => ({
        reservations,
        addOrUpdate,
        removeById,
        rooms,            // <--- Estado de salas
        addOrUpdateRoom,  // <--- Función para crear/editar salas
        deleteRoom ,       // <--- Función para borrar salas
        checkAvailability
    }), [reservations, rooms]);

    return <ReservationsCtx.Provider value={value}>{children}</ReservationsCtx.Provider>;
};

export const useReservations = () => {
    return useContext(ReservationsCtx);
};
