// src/components/DatePickerField.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import { DayPicker } from 'react-day-picker';
import 'react-day-picker/dist/style.css'; // base mínima, la overrideamos con Tailwind
import { format, parse, isValid } from 'date-fns';
import { es } from 'date-fns/locale';
import { CalendarDays } from 'lucide-react';

const fmt = 'yyyy-MM-dd';

function parseISO(s) {
  const d = parse(s, fmt, new Date());
  return isValid(d) ? d : undefined;
}

export default function DatePickerField({
  value,                // string 'yyyy-MM-dd'
  onChange,             // (newValue: string) => void
  placeholder = 'dd/mm/aaaa',
  id,
  name = 'date',
  required,
  disabled,
}) {
  const [open, setOpen] = useState(false);
  const ref = useRef(null);

  // En móviles mantenemos el input nativo (mejor UX/teclado)
  const isMobile = useMemo(() => {
    if (typeof window === 'undefined') return false;
    return window.matchMedia?.('(pointer: coarse)').matches;
  }, []);

  // Cerrar al hacer clic fuera
  useEffect(() => {
    const onDoc = (e) => {
      if (!ref.current) return;
      if (!ref.current.contains(e.target)) setOpen(false);
    };
    if (open) document.addEventListener('mousedown', onDoc);
    return () => document.removeEventListener('mousedown', onDoc);
  }, [open]);

  const selected = value ? parseISO(value) : undefined;

  if (isMobile) {
    // Nativo en mobile: lo estilizamos como los demás inputs
    return (
      <div className="relative">
        <input
          id={id}
          name={name}
          type="date"
          required={required}
          disabled={disabled}
          value={value || ''}
          onChange={(e) => onChange?.(e.target.value)}
          className="mt-1.5 w-full h-11 rounded-xl px-3.5
                     bg-white dark:bg-[#0F1318]
                     border border-gray-300 dark:border-[#2A3342]
                     text-gray-900 dark:text-gray-100
                     placeholder:text-gray-400 dark:placeholder:text-gray-500
                     focus:outline-none focus:ring-2 focus:ring-blue-500/30"
          placeholder={placeholder}
        />
        <CalendarDays className="w-4 h-4 absolute right-3 top-1/2 -translate-y-1/2 opacity-70 pointer-events-none" />
      </div>
    );
  }

  // Escritorio: campo de texto + popover con DayPicker
  return (
    <div className="relative" ref={ref}>
      <button
        type="button"
        id={id}
        className="mt-1.5 w-full h-11 rounded-xl px-3.5 text-left
                   bg-white dark:bg-[#0F1318]
                   border border-gray-300 dark:border-[#2A3342]
                   text-gray-900 dark:text-gray-100
                   focus:outline-none focus:ring-2 focus:ring-blue-500/30
                   flex items-center justify-between"
        onClick={() => setOpen((v) => !v)}
        aria-haspopup="dialog"
        aria-expanded={open}
      >
        <span className={value ? '' : 'text-gray-400 dark:text-gray-500'}>
          {value ? format(parseISO(value), 'dd/MM/yyyy') : placeholder}
        </span>
        <CalendarDays className="w-4 h-4 opacity-80" />
      </button>

      {open && (
        <div
          role="dialog"
          className="absolute z-50 mt-2 w-[320px] rounded-xl border
                     border-gray-200 dark:border-[#232A36]
                     bg-white dark:bg-[#171B22]
                     shadow-lg p-2"
        >
          <DayPicker
            mode="single"
            locale={es}
            selected={selected}
            onSelect={(d) => {
              if (!d) return;
              const iso = format(d, fmt);
              onChange?.(iso);
              setOpen(false);
            }}
            weekStartsOn={1}
            showOutsideDays
            // Clases Tailwind para tema claro/oscuro
            styles={{
              caption: { color: 'inherit' },
            }}
            classNames={{
              months: 'p-2',
              caption: 'flex justify-between items-center px-2 py-1 text-sm font-medium text-gray-800 dark:text-gray-100',
              nav: 'flex items-center gap-1',
              button_next: 'p-1 rounded-md hover:bg-gray-100 dark:hover:bg-[#141922]',
              button_previous: 'p-1 rounded-md hover:bg-gray-100 dark:hover:bg-[#141922]',
              table: 'mt-2 w-full border-collapse',
              head_row: 'text-xs text-gray-500 dark:text-gray-400',
              head_cell: 'w-8 h-8 font-medium',
              row: '',
              cell: 'text-sm p-0 text-center align-middle',
              day: 'w-8 h-8 rounded-md grid place-items-center hover:bg-gray-100 dark:hover:bg-[#141922] focus:outline-none',
              day_selected:
                'bg-blue-600 text-white hover:bg-blue-600 focus:bg-blue-600',
              day_today:
                'relative ring-1 ring-blue-400/40',
              day_outside: 'text-gray-300 dark:text-gray-500',
              day_disabled: 'opacity-40',
            }}
          />
        </div>
      )}

      {/* input oculto para formularios/autofill */}
      <input type="hidden" name={name} value={value || ''} required={required} />
    </div>
  );
}
