// src/components/ReservationForm.jsx
import React, { useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import { CalendarDays, Clock, Users, Type, MapPin, Save, X } from 'lucide-react';
import DatePickerField from '../components/DatePickerField';
import ConfirmationModal from './ui/ConfirmationModal';
import TimePickerField from './TimePickerField';
// Importamos el hook del contexto para traer las salas dinámicas
import { useReservations } from '../context/ReservationsContext'; 

// Función para formatear la hora (Global)
const formatTime = (date) => date.toLocaleTimeString('en-US', {
    hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'America/Bogota'
});

// ✅ FUNCIÓN AUXILIAR AJUSTADA: Recibe 'roomsList' dinámicamente
const getInitialValues = (reservationToEdit, userName, roomsList) => {
    // Protección: Si no hay salas cargadas, evitamos error accediendo a [0]
    // Si roomsList es undefined o vacío, defaultRoomId será cadena vacía.
    const safeRooms = roomsList || [];
    const defaultRoomId = safeRooms.length > 0 ? safeRooms[0].id : '';

    // Modo Creación (default)
    if (!reservationToEdit) {
        return {
            id: undefined, 
            title: '', 
            roomId: defaultRoomId, // Usa la primera sala de la lista dinámica
            date: '', 
            start: '09:00',
            end: '10:00', 
            description: '', 
            attendees: '', 
            location: '', 
            user: userName || '',
        };
    }

    // Modo Edición
    const start = new Date(reservationToEdit.startTime);
    const end = new Date(reservationToEdit.endTime);

    return {
        id: reservationToEdit.id,
        title: reservationToEdit.title || '',
        // Si la sala de la reserva ya no existe en la lista, usamos el default
        roomId: reservationToEdit.room?.id || defaultRoomId,
        date: start.toISOString().slice(0, 10),
        start: formatTime(start),
        end: formatTime(end),
        description: reservationToEdit.description || '',
        attendees: (Array.isArray(reservationToEdit.attendees) ? reservationToEdit.attendees.join(', ') : reservationToEdit.attendees) || '',
        location: reservationToEdit.location || '',
        user: reservationToEdit.user || userName || '',
    };
};

export default function ReservationForm({
  userName,
  reservationToEdit,
  onUpsert,
  onCancel,
}) {

  // 1. OBTENER SALAS DEL CONTEXTO (Ya no usamos la constante ROOMS manual)
  const { rooms, checkAvailability } = useReservations(); 

  // 2. INICIALIZAR ESTADO 
  // Pasamos 'rooms' (que viene del contexto) a la función auxiliar
  const [values, setValues] = useState(() => 
    getInitialValues(reservationToEdit, userName, rooms)
  );

  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState({});
  const [confirmOpen, setConfirmOpen] = useState(false);
  const [confirmData, setConfirmData] = useState(null);
  const [busySlots, setBusySlots] = useState([]);
  const [checkingAvailability, setCheckingAvailability] = useState(false);


  // 3. EFECTO: Consultar disponibilidad cuando cambia Sala o Fecha
  useEffect(() => {
    // Solo consultamos si tenemos fecha y sala seleccionada
    if (!values.date || !values.roomId) {
        setBusySlots([]);
        return;
    }

    const fetchBusySlots = async () => {
        setCheckingAvailability(true);
        // Buscamos el email de la sala actual (usando la lista rooms del contexto)
        const currentRoom = rooms.find(r => r.id === values.roomId);

        if (currentRoom?.email) {
            const slots = await checkAvailability(currentRoom.email, values.date);
            setBusySlots(slots);
        } else {
            setBusySlots([]);
        }
        setCheckingAvailability(false);
    };

    // Pequeño debounce para no saturar si cambia rápido
    const timer = setTimeout(fetchBusySlots, 500);
    return () => clearTimeout(timer);
  }, [values.date, values.roomId, rooms, checkAvailability]);

  // 3. BUSCAR LA SALA EN LA LISTA DINÁMICA 'rooms'
  const room = useMemo(() => {
      if (!rooms) return null;
      return rooms.find(r => r.id === values.roomId);
  }, [rooms, values.roomId]);

  const handle = (k) => (e) => setValues(v => ({ ...v, [k]: e.target.value }));

  const validate = () => {
    const e = {};
    if (!values.title.trim()) e.title = 'El título es obligatorio.';
    if (!values.date) e.date = 'Selecciona una fecha.';
    if (!values.start || !values.end) e.time = 'Indica hora de inicio y fin.';
    if (values.date && values.start && values.end) {
      const s = new Date(`${values.date}T${values.start}:00`);
      const f = new Date(`${values.date}T${values.end}:00`);
      if (f <= s) e.time = 'La hora de fin debe ser mayor a la de inicio.';
    }
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const persist = async (evt) => {
    evt.preventDefault();
    if (!validate()) return;
    setSaving(true);
    try {
      const startTime = new Date(`${values.date}T${values.start}:00`);
      const endTime = new Date(`${values.date}T${values.end}:00`);

      // Protección por si 'room' es undefined (ej. lista vacía)
      const selectedRoom = room || (rooms && rooms.length > 0 ? rooms[0] : { nombre: 'Sin sala', email: '' });

      const payload = {
        id: values.id,
        title: values.title.trim(),
        room: selectedRoom,
        roomEmail: selectedRoom.email,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        description: values.description.trim(),
        attendees: values.attendees
          ? values.attendees.split(',').map(s => s.trim()).filter(Boolean)
          : [],
        location: values.location.trim(),
        user: values.user,
      };
      await onUpsert?.(payload);

      setConfirmData ({
        title: payload.title,
        roomName: payload.room?.nombre || 'Sala',
        start: payload.startTime,
        end: payload.endTime,
      });
      setConfirmOpen(true);
    } finally {
      setSaving(false);
    }
  };

  return (
    <motion.form
      onSubmit={persist}
      initial={{ opacity: 0, y: 6 }}
      animate={{ opacity: 1, y: 0 }}
      className="rounded-2xl p-5 md:p-6 lg:p-7 space-y-6 bg-white dark:bg-[#171B22] border border-gray-200 dark:border-[#232A36] shadow-sm"
    >
      {/* Encabezado */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h3 className="text-xl md:text-2xl font-semibold tracking-tight text-gray-900 dark:text-white">
            {values.id ? 'Editar reserva' : 'Nueva reserva'}
          </h3>
          <p className="mt-1.5 text-sm text-gray-600 dark:text-gray-400">
            Completa los campos y guarda los cambios.
          </p>
        </div>
        {onCancel && (
          <button type="button" onClick={onCancel} className="h-10 px-3 inline-flex items-center gap-2 rounded-xl bg-gray-50 dark:bg-[#0F1318] border border-gray-200 dark:border-[#2A3342] text-gray-800 dark:text-gray-100 hover:bg-gray-100 dark:hover:bg-[#141922] transition">
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6">
        {/* Título */}
        <div className="md:col-span-2">
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Type className="w-4 h-4 opacity-80" /> Título*
          </label>
          <input
            value={values.title}
            onChange={handle('title')}
            className={`mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border ${errors.title ? 'border-red-500' : 'border-gray-300 dark:border-[#2A3342]'} text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 ${errors.title ? 'focus:ring-red-500/40' : 'focus:ring-blue-500/30'}`}
            placeholder="Breve nombre de la reunión"
          />
          {errors.title && <p className="mt-1 text-xs text-red-500">{errors.title}</p>}
        </div>

        {/* SELECTOR DE SALA DINÁMICO */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <MapPin className="w-4 h-4 opacity-80" /> Sala
          </label>
          <select
            value={values.roomId}
            onChange={handle('roomId')}
            className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30"
          >
            {/* Iteramos sobre 'rooms' del contexto. Si está vacío, no muestra opciones */}
            {rooms && rooms.map(r => (
              <option key={r.id} value={r.id}>{r.nombre}</option>
            ))}
          </select>
        </div>

        {/* Fecha */}
        <div>
           <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
              <CalendarDays className="w-4 h-4 opacity-80" /> Fecha*
           </label>
           <DatePickerField id="date" value={values.date} onChange={(v) => setValues(s => ({ ...s, date: v }))} required />
           {errors.date && <p className="mt-1 text-xs text-red-500">{errors.date}</p>}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6 pt-2">
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Clock className="w-4 h-4 opacity-80" /> Hora inicio*
          </label>
          <TimePickerField value={values.start} onChange={(val) => setValues(v => ({...v, start: val}))} use12h={false} minuteStep={30} />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Clock className="w-4 h-4 opacity-80" /> Hora fin*
          </label>
          <TimePickerField value={values.end} onChange={(val) => setValues(v => ({...v, end: val}))} use12h={false} minuteStep={30} />
          {errors.time && <p className="mt-1 text-xs text-red-500">{errors.time}</p>}
        </div>
      </div>

      <div className="grid grid-cols-1 gap-5 md:gap-6 pt-2">
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">Descripción</label>
          <textarea rows={3} value={values.description} onChange={handle('description')} className="mt-1.5 w-full rounded-xl p-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="Contexto breve..." />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Users className="w-4 h-4 opacity-80" /> Invitados
          </label>
          <input value={values.attendees} onChange={handle('attendees')} className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="ana@ejemplo.com, juan@ejemplo.com" />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <MapPin className="w-4 h-4 opacity-80" /> Ubicación (opcional)
          </label>
          <input value={values.location} onChange={handle('location')} className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="Enlace o lugar físico" />
        </div>
      </div>

      <div className="pt-3 mt-1 flex items-center justify-end gap-2">
        {onCancel && (
          <button type="button" onClick={onCancel} className="h-10 px-3 inline-flex items-center gap-2 rounded-xl bg-gray-50 dark:bg-[#0F1318] border border-gray-200 dark:border-[#2A3342] text-gray-800 dark:text-gray-100 hover:bg-gray-100 dark:hover:bg-[#141922]">
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}

        <ConfirmationModal
          open={confirmOpen}
          onClose={() => setConfirmOpen(false)}
          data={confirmData || {}}
          onCTA={{ primary: () => { setConfirmOpen(false); window.location.href = '/my-reservations'; } }}
        />

        <motion.button type="submit" disabled={saving} whileTap={{ scale: saving ? 1 : 0.98 }} className={`h-10 px-4 inline-flex items-center gap-2 rounded-xl text-white font-semibold shadow ${saving ? 'bg-blue-500/70 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-500'} ring-1 ring-blue-400/30`}>
          <Save className="w-4 h-4" />
          {saving ? 'Guardando…' : values.id ? 'Guardar cambios' : 'Crear reserva'}
        </motion.button>
      </div>
    </motion.form>
  );
}
