import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Plus, Edit, Trash } from 'lucide-react';
import { format, addHours } from 'date-fns';
import { useNavigate } from 'react-router-dom';

import { generateId, hasRoomConflict } from '../utils/reservations';

// Outlook / Graph
import { msalInstance } from '../msalConfig';
import {
  graphCreateEvent,
  graphUpdateEvent,
  graphHasConflict,
} from '../utils/graph';

// Catálogo local de salas (si ya lo exportas desde utils, impórtalo en lugar de esto)
const rooms = [
  { id: '1', nombre: "Círculo de Sabios I", capacidad: 5 },
  { id: '2', nombre: "Círculo de Sabios II", capacidad: 5 },
  { id: '3', nombre: "Círculo de Sabios III", capacidad: 5 },
  { id: '4', nombre: "Consejo de la Tribu", capacidad: 12 },
  { id: '5', nombre: "Plaza de Conexiones", capacidad: 6 },
];

const ReservationForm = ({
  userName,
  reservationToEdit = null,
  allReservations = [],
  onUpsert, // del Context: addOrUpdate
}) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    room: rooms[0],
    startTime: new Date(),
    duration: 1, // horas
  });

  const [editData, setEditData] = useState(null);
  const [error, setError] = useState('');
  const [syncInfo, setSyncInfo] = useState(''); // mensajes suaves de sincronización

  // Cargar datos al editar
  useEffect(() => {
    if (reservationToEdit) {
      setEditData(reservationToEdit);
      setFormData({
        title: reservationToEdit.title || '',
        description: reservationToEdit.description || '',
        room: reservationToEdit.room || rooms[0],
        startTime: new Date(reservationToEdit.startTime),
        duration: Math.max(
          0.25,
          (new Date(reservationToEdit.endTime) - new Date(reservationToEdit.startTime)) / (1000 * 60 * 60)
        ),
      });
    } else {
      setEditData(null);
      setFormData({
        title: '',
        description: '',
        room: rooms[0],
        startTime: new Date(),
        duration: 1,
      });
    }
    setError('');
    setSyncInfo('');
  }, [reservationToEdit]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSyncInfo('');

    const start = new Date(formData.startTime);
    const end = addHours(start, Number(formData.duration || 1));

    // Validaciones básicas
    if (!formData.title.trim()) {
      setError('El título es obligatorio.');
      return;
    }
    if (end <= start) {
      setError('La hora de fin debe ser mayor a la de inicio.');
      return;
    }
    if (formData.duration < 0.25 || formData.duration > 8) {
      setError('La duración debe estar entre 15 minutos y 8 horas.');
      return;
    }

    // Validación de conflicto local (misma sala / solape)
    const payloadPreview = {
      id: editData?.id || generateId(),
      room: formData.room,
      startTime: start,
      endTime: end,
    };
    const localConflict = hasRoomConflict(allReservations, {
      id: payloadPreview.id,
      roomId: payloadPreview.room.id,
      startTime: payloadPreview.startTime,
      endTime: payloadPreview.endTime,
    });
    if (localConflict) {
      setError('⚠️ La sala ya está ocupada en ese rango horario. Elige otro horario o sala.');
      return;
    }

    // Construir payload final
    const payload = {
      id: payloadPreview.id,
      user: userName,
      room: formData.room,
      startTime: start,
      endTime: end,
      title: formData.title.trim(),
      description: formData.description?.trim() || '',
      status: editData?.status || 'pending',
      outlookEventId: editData?.outlookEventId || undefined,
    };

    // Sincronización con Outlook (si hay sesión MSAL)
    const account = msalInstance.getActiveAccount() || msalInstance.getAllAccounts()[0];

    if (account) {
      try {
        // (Opcional) conflicto en Outlook del usuario (cualquier evento suyo)
        const conflictOutlook = await graphHasConflict(
          payload.startTime.toISOString(),
          payload.endTime.toISOString()
        );
        if (conflictOutlook) {
          setError('⚠️ Ya tienes un evento en tu Outlook en ese rango. Cambia el horario.');
          return;
        }

        // Crear o actualizar evento en Outlook
        if (payload.outlookEventId) {
          const ev = await graphUpdateEvent(payload.outlookEventId, payload);
          payload.outlookEventId = ev?.id || payload.outlookEventId;
          setSyncInfo('Evento actualizado en Outlook.');
        } else {
          const ev = await graphCreateEvent(payload);
          payload.outlookEventId = ev?.id;
          setSyncInfo('Evento creado en Outlook.');
        }
      } catch (e) {
        // No bloqueamos el guardado local, pero avisamos
        console.error('Graph error:', e);
        setSyncInfo('No se pudo sincronizar con Outlook ahora. Guardado localmente.');
      }
    } else {
      // Sin sesión: guardado local y mensaje suave
      setSyncInfo('Reserva guardada localmente. Inicia sesión para sincronizar con Outlook.');
    }

    // Guardar en Context (persistente en localStorage por el Provider)
    onUpsert?.(payload);

    // Reset si era creación
    if (!editData) {
      setFormData({
        title: '',
        description: '',
        room: rooms[0],
        startTime: new Date(),
        duration: 1,
      });
    } else {
      // Si era edición, puedes navegar o limpiar edit
      // navigate('/my-reservations');
    }
  };

  return (
    <motion.div
      className="bg-white/80 backdrop-blur-xl border border-gray-200/50 rounded-3xl p-6 shadow-xl"
      initial={{ opacity: 0, y: 8 }}
      animate={{ opacity: 1, y: 0 }}
    >
      <div className="flex items-center gap-2 mb-4">
        {editData ? (
          <>
            <Edit className="w-5 h-5 text-blue-600" />
            <h3 className="text-lg font-semibold">Editar Reserva</h3>
          </>
        ) : (
          <>
            <Plus className="w-5 h-5 text-green-600" />
            <h3 className="text-lg font-semibold">Nueva Reserva</h3>
          </>
        )}
      </div>

      {error && (
        <div className="mb-4 text-sm text-red-700 bg-red-50 border border-red-200 rounded-xl p-3">
          {error}
        </div>
      )}
      {syncInfo && !error && (
        <div className="mb-4 text-sm text-blue-700 bg-blue-50 border border-blue-200 rounded-xl p-3">
          {syncInfo}
        </div>
      )}

      <form onSubmit={handleSubmit} className="grid md:grid-cols-2 gap-4">
        {/* Título */}
        <div className="md:col-span-2">
          <label className="block text-sm text-gray-600 mb-1">Título</label>
          <input
            value={formData.title}
            onChange={e => setFormData({ ...formData, title: e.target.value })}
            className="w-full rounded-xl border border-gray-200 px-3 py-2 outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Ej. Reunión de proyecto"
            required
          />
        </div>

        {/* Sala */}
        <div>
          <label className="block text-sm text-gray-600 mb-1">Sala</label>
          <select
            value={formData.room.id}
            onChange={(e) => {
              const r = rooms.find(x => x.id === e.target.value);
              setFormData({ ...formData, room: r || rooms[0] });
            }}
            className="w-full rounded-xl border border-gray-200 px-3 py-2 outline-none focus:ring-2 focus:ring-blue-500"
          >
            {rooms.map(r => (
              <option key={r.id} value={r.id}>
                {r.nombre} — {r.capacidad} pax
              </option>
            ))}
          </select>
        </div>

        {/* Inicio */}
        <div>
          <label className="block text-sm text-gray-600 mb-1">Inicio</label>
          <input
            type="datetime-local"
            value={format(formData.startTime, "yyyy-MM-dd'T'HH:mm")}
            onChange={(e) => setFormData({ ...formData, startTime: new Date(e.target.value) })}
            className="w-full rounded-xl border border-gray-200 px-3 py-2 outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Duración */}
        <div>
          <label className="block text-sm text-gray-600 mb-1">Duración (horas)</label>
          <input
            type="number"
            step="0.25"
            min="0.25"
            max="8"
            value={formData.duration}
            onChange={(e) => setFormData({ ...formData, duration: Number(e.target.value) })}
            className="w-full rounded-xl border border-gray-200 px-3 py-2 outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Descripción */}
        <div className="md:col-span-2">
          <label className="block text-sm text-gray-600 mb-1">Descripción</label>
          <textarea
            rows={3}
            value={formData.description}
            onChange={e => setFormData({ ...formData, description: e.target.value })}
            className="w-full rounded-xl border border-gray-200 px-3 py-2 outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Notas, agenda, invitados, etc."
          />
        </div>

        {/* Botones */}
        <div className="md:col-span-2 flex items-center gap-3">
          <motion.button
            type="submit"
            className="px-4 py-2 rounded-xl bg-blue-600 text-white font-medium shadow-md hover:bg-blue-700 transition"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            {editData ? 'Guardar cambios' : 'Crear reserva'}
          </motion.button>

          {editData && (
            <motion.button
              type="button"
              onClick={() => {
                setEditData(null);
                setFormData({ title: '', description: '', room: rooms[0], startTime: new Date(), duration: 1 });
                setError('');
                setSyncInfo('');
              }}
              className="flex items-center gap-2 text-red-600 hover:text-red-800"
              whileHover={{ scale: 1.05 }}
            >
              <Trash className="w-4 h-4" /> Cancelar edición
            </motion.button>
          )}
        </div>
      </form>
    </motion.div>
  );
};

export default ReservationForm;
