import React from 'react';
import { motion } from 'framer-motion';
import { NavLink } from 'react-router-dom';
import { Home, Calendar, Settings, User } from 'lucide-react';

const MotionNavLink = motion(NavLink);

const Header = () => {
  const navItems = [
    { path: '/', icon: Home, label: 'Crear Reserva' },
    { path: '/my-reservations', icon: User, label: 'Mis Reservas' },
    { path: '/admin', icon: Settings, label: 'Admin' },
  ];

  return (
    <motion.header
      className="bg-white/80 backdrop-blur-xl border-b border-gray-200/50 shadow-lg"
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="container mx-auto px-4 py-4 max-w-6xl">
        <div className="flex items-center justify-between">
          <motion.div
            className="flex items-center gap-3"
            whileHover={{ scale: 1.05 }}
          >
            <Calendar className="w-8 h-8 text-blue-600" />
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Salas FYCO
            </h1>
          </motion.div>

          <nav
            className="hidden md:flex items-center gap-2"
            role="navigation"
            aria-label="Navegación principal"
          >
            {navItems.map(({ path, icon: Icon, label }) => (
              <MotionNavLink
                key={path}
                to={path}
                // className en función del estado activo
                className={({ isActive }) =>
                  [
                    'flex items-center gap-2 px-4 py-2 rounded-xl font-medium transition-all duration-300',
                    isActive
                      ? 'bg-blue-100 text-blue-700 shadow-md'
                      : 'text-gray-700 hover:text-blue-700 hover:bg-blue-50',
                  ].join(' ')
                }
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                aria-label={label}
              >
                <Icon className="w-5 h-5" aria-hidden="true" />
                <span>{label}</span>
              </MotionNavLink>
            ))}
          </nav>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
