import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import { useReservations } from '../context/ReservationsContext';
import { useParams } from 'react-router-dom'; // üëà Para leer el ID de la URL

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  reservationToEdit,
  currentPath,
}) => {
  const { reservations, addOrUpdate } = useReservations();

  useEffect(() => {
    // Si necesitas alg√∫n efecto al montar, lo dejas aqu√≠.
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-gray-950 dark:via-slate-950 dark:to-black transition-colors">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />

      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Crear Reserva</h2>
          <motion.button
            onClick={toggleSidebar}
            className="p-2 rounded-lg hover:bg-gray-100"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6 text-gray-700" />
          </motion.button>
        </div>

        <ReservationForm
          userName={userName}
          reservationToEdit={reservationToEdit}
          // Estas dos props permiten que el Form valide choques y guarde:
          allReservations={reservations}
          onUpsert={addOrUpdate}
        />

        <OutlookSync />
      </motion.div>
    </div>
  );
};

export default CreateReservation;
