// src/context/ReservationsContext.js

import { createContext, useContext, useEffect, useMemo, useState, useCallback } from "react";
import { msalInstance, loginRequest } from '../msalConfig';

// Contexto
const ReservationsCtx = createContext(null);

// FUNCIÃ“N AUXILIAR DE RECONSTRUCCIÃ“N DE FECHAS (strings ISO -> Date objects)
const reviveDates = (reservations) => {
    if (!reservations || !Array.isArray(reservations)) return []; 

    return reservations.map(res => ({
        ...res,
        // Convertir las cadenas de tiempo ISO a objetos Date
        startTime: res.startTime ? new Date(res.startTime) : undefined,
        endTime: res.endTime ? new Date(res.endTime) : undefined,
    }));
};

// FunciÃ³n auxiliar para extraer nombre de sala de los asistentes de Outlook
const extractRoomName = (evt) => {
    // 1. Prioridad: Recurso en asistentes
    const roomAttendee = evt.attendees?.find(a => a.type === 'resource');
    if (roomAttendee) return roomAttendee.emailAddress?.name;

    // 2. Prioridad: UbicaciÃ³n
    if (evt.location?.displayName && evt.location.displayName !== "Sin sala") {
        return evt.location.displayName;
    }
    return 'Sala no especificada';
};

// DEFINICIÃ“N DE SALAS INICIALES (Con capacidad y recursos)
const INITIAL_ROOMS = [
  { id: '1', nombre: 'CÃ­rculo de Sabios I', email: 'circulo.sabios1@fycotelecom.com', capacidad: 8, recursos: ['TV / Monitor', 'Tablero'] },
  { id: '2', nombre: 'CÃ­rculo de Sabios II', email: 'circulo.sabios2@fycotelecom.com', capacidad: 6, recursos: ['TV / Monitor', 'HDMI'] },
  { id: '3', nombre: 'CÃ­rculo de Sabios III', email: 'circulo.sabios3@fycotelecom.com', capacidad: 4, recursos: ['Tablero'] },
  { id: '4', nombre: 'Consejo de la Tribu', email: 'consejo.tribu@fycotelecom.com', capacidad: 12, recursos: ['Video Conferencia', 'TV / Monitor', 'Aire Acondicionado'] },
  { id: '5', nombre: 'Plaza de Conexiones', email: 'plaza.conexiones@fycotelecom.com', capacidad: 20, recursos: ['Proyector', 'Sonido'] },
];

export const ReservationsProvider = ({ children }) => {
    // 1. ESTADO DE RESERVAS
    const [reservations, setReservations] = useState([]);

    // âœ… ESTADO PARA FORZAR LA RECARGA DESDE OUTLOOK
    const [refreshTick, setRefreshTick] = useState(0); 

    // 2. ESTADO DE SALAS (Cargado desde Local Storage o default)
    const [rooms, setRooms] = useState(() => {
        try {
            const saved = localStorage.getItem('app_rooms');
            return saved ? JSON.parse(saved) : INITIAL_ROOMS;
        } catch (e) {
            console.error("Error al cargar salas:", e);
            return INITIAL_ROOMS;
        }
    });

    // =========================================================================
    // 3. GESTIÃ“N DE SALAS (ROOMS CRUD)
    // =========================================================================

    // Guardar en Local Storage cada vez que cambien las salas
    useEffect(() => {
        localStorage.setItem('app_rooms', JSON.stringify(rooms));
    }, [rooms]);

    // FunciÃ³n: Agregar o Editar Sala
    const addOrUpdateRoom = useCallback((room) => {
        setRooms(prev => {
            const exists = prev.find(r => r.id === room.id);
            if (exists) {
                // Actualizar existente
                return prev.map(r => r.id === room.id ? room : r);
            } else {
                // Crear nueva (Generar ID si es 'NEW' o viene vacÃ­o)
                const newId = (room.id === 'NEW' || !room.id) ? Date.now().toString() : room.id;
                const newRoom = { ...room, id: newId };
                return [...prev, newRoom];
            }
        });
    }, []);

    // FunciÃ³n: Eliminar Sala
    const deleteRoom = useCallback((roomId) => {
        setRooms(prev => prev.filter(r => r.id !== roomId));
    }, []);

    // =========================================================================
    // 4. VERIFICAR DISPONIBILIDAD (getSchedule)
    // =========================================================================
    const checkAvailability = useCallback(async (roomEmail, dateString) => {
        const account = msalInstance.getActiveAccount();
        if (!account || !roomEmail) return [];

        try {
            const tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
            const token = tokenResponse.accessToken;

            const startDateTime = `${dateString}T00:00:00`;
            const endDateTime = `${dateString}T23:59:59`;

            const payload = {
                schedules: [roomEmail],
                startTime: { dateTime: startDateTime, timeZone: "America/Bogota" },
                endTime: { dateTime: endDateTime, timeZone: "America/Bogota" },
                availabilityViewInterval: 15
            };

            const response = await fetch('https://graph.microsoft.com/v1.0/me/calendar/getSchedule', {
                method: 'POST',
                headers: { 
                    'Authorization': `Bearer ${token}`, 
                    'Content-Type': 'application/json',
                    'Prefer': 'outlook.timezone="America/Bogota"' 
                },
                body: JSON.stringify(payload)
            });

            if (!response.ok) return [];

            const data = await response.json();
            const schedule = data.value?.[0]?.scheduleItems || [];

            return schedule.filter(item => item.status !== 'free').map(item => ({
                start: item.start.dateTime, 
                end: item.end.dateTime
            }));

        } catch (error) {
            console.error("Error verificando disponibilidad:", error);
            return [];
        }
    }, []);


    // =========================================================================
    // 5. CARGA INICIAL DE DATOS (GET)
    // =========================================================================
    useEffect(() => {
        async function fetchReservations() {
            const account = msalInstance.getActiveAccount();
            if (!account) return; 

            try {
                const tokenResponse = await msalInstance.acquireTokenSilent({
                    ...loginRequest,
                    account: account
                });
                const token = tokenResponse.accessToken;

                // Solicitamos datos bÃ¡sicos del evento
                const response = await fetch('https://graph.microsoft.com/v1.0/me/events?$select=subject,start,end,location,id,attendees,organizer,bodyPreview&$top=50', {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Prefer': 'outlook.timezone="America/Bogota"'
                    }
                });

                if (!response.ok) throw new Error(`Error Graph: ${response.status}`);

                const graphData = await response.json();

                const mapped = (graphData.value || []).map(evt => ({
                    id: evt.id,
                    title: evt.subject,
                    startTime: evt.start.dateTime, 
                    endTime: evt.end.dateTime,
                    room: { nombre: extractRoomName(evt) },
                    user: evt.organizer?.emailAddress.name || evt.organizer?.emailAddress.address,
                    attendees: evt.attendees || [],
                    location: evt.location?.displayName || ''
                }));

                // APLICAMOS REVIVEDATES AL CARGAR DATOS
                setReservations(reviveDates(mapped));

            } catch (error) {
                console.error("Error cargando reservas:", error);
            }
        }

        // âœ… DEPENDEMOS DE REFRESH TICK
        fetchReservations();
    }, [refreshTick]); // ðŸ‘ˆ El useEffect se ejecuta cada vez que refreshTick cambia

    // =========================================================================
    // 6. CREAR O MODIFICAR (POST / PATCH)
    // =========================================================================

const addOrUpdate = useCallback(async (reservationPayload) => {
    const isModification = !!reservationPayload.id;
    const reservationId = reservationPayload.id;

    // âœ… LOG 1: Vemos el Payload antes de procesar
    console.log("1. PAYLOAD RECIBIDO DEL FORMULARIO:", reservationPayload); 

    const account = msalInstance.getActiveAccount();
    if (!account) throw new Error("Sin sesiÃ³n activa");

    let tokenResponse; 
    try {
        tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
    } catch {
        // No deberÃ­a pasar si el loginRedirect funciona, pero es una protecciÃ³n
        msalInstance.loginRedirect(loginRequest);
        return;
    }
    const token = tokenResponse.accessToken;

    const url = isModification 
        ? `https://graph.microsoft.com/v1.0/me/events/${reservationId}` 
        : 'https://graph.microsoft.com/v1.0/me/events';
    const method = isModification ? 'PATCH' : 'POST';

    // Este es el cuerpo que se envÃ­a a Microsoft Graph
    const event = {
        subject: reservationPayload.title,
        // CRÃTICO: Las fechas deben ser cadenas ISO correctas.
        start: { dateTime: reservationPayload.startTime, timeZone: "America/Bogota" },
        end: { dateTime: reservationPayload.endTime, timeZone: "America/Bogota" },
        location: { displayName: reservationPayload.room.nombre }, 
        attendees: [{
            emailAddress: { address: reservationPayload.roomEmail, name: reservationPayload.room.nombre },
            type: "resource"
        }]
    };

    // âœ… LOG 2: Vemos el cuerpo exacto del evento antes de enviar
    console.log("2. CUERPO DE EVENTO GRAPH ENVIADO:", event); 

    const response = await fetch(url, {
        method: method,
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
        body: JSON.stringify(event)
    });

    // âœ… LOG 3: Vemos la respuesta HTTP
    console.log("3. RESPUESTA HTTP STATUS:", response.status); 

    if (!response.ok) {
        const err = await response.json();
        // âœ… LOG 4: Vemos el error detallado de Microsoft si falla
        console.error("4. ERROR DETALLADO DE GRAPH:", err); 
        throw new Error(err.error?.message || "Error en la API");
    }

    // Ã‰XITO: Forzamos la recarga de toda la lista
    const apiResponse = await response.json();
    setRefreshTick(prev => prev + 1); 

    return apiResponse;
}, []);

    // =========================================================================
    // 7. ELIMINAR (DELETE)
    // =========================================================================
    const removeById = useCallback(async (reservationId) => {
        const account = msalInstance.getActiveAccount();
        if (!account) return;

        const tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
        const token = tokenResponse.accessToken;

        const response = await fetch(`https://graph.microsoft.com/v1.0/me/events/${reservationId}`, {
            method: 'DELETE',
            headers: { 'Authorization': `Bearer ${token}` }
        });

        if (response.ok) {
            // âœ… FORZAMOS LA RECARGA DESPUÃ‰S DE ELIMINAR
            setRefreshTick(prev => prev + 1);
            // setReservations(prev => prev.filter(r => r.id !== reservationId)); // Eliminamos el set manual
        }
    }, []);

    // Exponemos todo en el valor del contexto
    const value = useMemo(() => ({
        reservations,
        addOrUpdate,
        removeById,
        rooms,            
        addOrUpdateRoom, 
        deleteRoom,      
        checkAvailability
    }), [reservations, rooms, refreshTick, addOrUpdate, removeById, addOrUpdateRoom, deleteRoom, checkAvailability]);

    return <ReservationsCtx.Provider value={value}>{children}</ReservationsCtx.Provider>;
};

export const useReservations = () => {
    return useContext(ReservationsCtx);
};
