import React from 'react';
import { motion } from 'framer-motion';
import { Home, Calendar, Settings, User, X } from 'lucide-react';

const Sidebar = ({ isOpen, onClose, currentPath }) => {
  const navItems = [
    { path: '/', icon: Home, label: 'Crear Reserva' },
    { path: '/my-reservations', icon: User, label: 'Mis Reservas' },
    { path: '/admin', icon: Settings, label: 'Admin' }
  ];

  return (
    <>
      <motion.div
        className={`fixed inset-y-0 left-0 z-50 w-64 bg-white/90 backdrop-blur-xl border-r border-gray-200/50 shadow-xl transform ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } md:hidden`}
        initial={false}
        animate={{ x: isOpen ? 0 : -256 }}
        transition={{ duration: 0.3 }}
      >
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <div className="flex items-center gap-3">
            <Calendar className="w-6 h-6 text-blue-600" />
            <h2 className="text-xl font-bold text-gray-800">Salas FYCO</h2>
          </div>
          <motion.button
            onClick={onClose}
            className="p-2 rounded-lg hover:bg-gray-100"
            whileTap={{ scale: 0.95 }}
          >
            <X className="w-6 h-6 text-gray-600" />
          </motion.button>
        </div>
        <nav className="p-4 space-y-4">
          {navItems.map((item) => {
            const Icon = item.icon;
            const isActive = currentPath === item.path;
            return (
              <motion.a
                key={item.path}
                href={item.path}
                onClick={onClose}
                className={`flex items-center gap-3 px-4 py-3 rounded-xl font-medium transition-all duration-300 ${
                  isActive
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-gray-600 hover:text-blue-600 hover:bg-blue-50'
                }`}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <Icon className="w-5 h-5 flex-shrink-0" />
                <span className="text-sm">{item.label}</span>
              </motion.a>
            );
          })}
        </nav>
      </motion.div>
      {isOpen && (
        <motion.div
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        />
      )}
    </>
  );
};

export default Sidebar;
