// src/components/ThemeToggle.jsx
import React from 'react';
import { Sun, Moon } from 'lucide-react';
import { useDarkMode } from '../hooks/useDarkMode';
import { motion, AnimatePresence, useReducedMotion } from 'framer-motion';

export default function ThemeToggle() {
  const { isDark, toggle } = useDarkMode();
  const reduce = useReducedMotion();

  // Animación muy suave (ease)
  const variants = {
    initial: { opacity: 0, scale: reduce ? 1 : 0.995, rotate: reduce ? 0 : -2 },
    animate: { opacity: 1, scale: 1, rotate: 0 },
    exit:    { opacity: 0, scale: reduce ? 1 : 0.995, rotate: reduce ? 0 : 2 },
  };

  const transition = { duration: 0.22, ease: 'easeInOut' };

  return (
    <button
      onClick={toggle}
      className="p-2 rounded-xl border border-gray-200 dark:border-dark-border
                 bg-gray-50 dark:bg-dark-surface hover:bg-gray-100 dark:hover:bg-dark-border
                 focus:outline-none focus:ring-2 focus:ring-blue-400/40 dark:focus:ring-blue-500/30
                 transition"
      title={isDark ? 'Cambiar a claro' : 'Cambiar a oscuro'}
      aria-label={isDark ? 'Cambiar a claro' : 'Cambiar a oscuro'}
      aria-pressed={isDark}
      type="button"
    >
      <div className="relative w-5 h-5">
        <AnimatePresence mode="wait" initial={false}>
          {isDark ? (
            <motion.span
              key="sun"
              variants={variants}
              initial="initial"
              animate="animate"
              exit="exit"
              transition={transition}
              className="absolute inset-0 grid place-items-center"
            >
              <Sun className="w-5 h-5 text-yellow-400" />
            </motion.span>
          ) : (
            <motion.span
              key="moon"
              variants={variants}
              initial="initial"
              animate="animate"
              exit="exit"
              transition={transition}
              className="absolute inset-0 grid place-items-center"
            >
              <Moon className="w-5 h-5 text-blue-400" />
            </motion.span>
          )}
        </AnimatePresence>
      </div>
    </button>
  );
}
