// src/components/ReservationList.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CalendarDays, Edit3, Trash2, Users, Cloud, HardDrive, RotateCw } from 'lucide-react';
import { resyncReservationToOutlook } from '../utils/resyncOutlook';
import { downloadICS } from '../utils/ics';
import { format } from 'date-fns';

// nuevo prop opcional
const ReservationList = ({
  reservations = [],
  onEdit,
  onDelete,
  onResync,           // ðŸ‘ˆ NUEVO (opcional)
  isAdmin = false,
  userName,
}) => {
  const [deletingId, setDeletingId] = useState(null);
  const [syncingId, setSyncingId] = useState(null);
  // ...

  const handleDelete = async (id) => {
    if (deletingId) return;
    const confirm = window.confirm('Â¿EstÃ¡s seguro de eliminar esta reserva?');
    if (!confirm) return;
    try {
      setDeletingId(id);
      await onDelete?.(id);
    } finally {
      setDeletingId(null);
    }
  };

  const Item = ({ r }) => {
    const isMine = !isAdmin ? r.user === userName : true;
    const outlook = !!r.outlookEventId;
    const attendeesCount = Array.isArray(r.attendees) ? r.attendees.length : 0;

    return (
      <motion.li
        className="bg-white/80 dark:bg-white/5 backdrop-blur-xl border border-gray-200/50 dark:border-gray-800/50 rounded-2xl p-4 shadow-sm transition-colors"
        initial={{ opacity: 0, y: 6 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <div className="flex items-start justify-between gap-3">
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 flex-wrap">
              <h4 className="text-base md:text-lg font-semibold text-gray-800 truncate">
                {r.title}
              </h4>

              {/* Chip Outlook / Local */}
              {outlook ? (
                <span className="inline-flex items-center gap-1 px-2 py-0.5 text-xs rounded-full bg-green-100 text-green-700">
                  <Cloud className="w-3.5 h-3.5" /> Outlook
                </span>
              ) : (
                <span className="inline-flex items-center gap-1 px-2 py-0.5 text-xs rounded-full bg-gray-100 text-gray-700">
                  <HardDrive className="w-3.5 h-3.5" /> Local
                </span>
              )}

              {/* Chip invitados */}
              {attendeesCount > 0 && (
                <span className="inline-flex items-center gap-1 px-2 py-0.5 text-xs rounded-full bg-indigo-100 text-indigo-700">
                  <Users className="w-3.5 h-3.5" /> {attendeesCount} invitado{attendeesCount > 1 ? 's' : ''}
                </span>
              )}
            </div>

            <div className="mt-1 text-sm text-gray-600">
              <div className="flex items-center gap-2">
                <CalendarDays className="w-4 h-4 text-blue-600" />
                <span className="truncate">
                  {r.room?.nombre || 'Sala'} Â· {format(new Date(r.startTime), "dd/MM/yyyy HH:mm")} â€“ {format(new Date(r.endTime), "HH:mm")}
                </span>
              </div>
              {r.description && (
                <p className="mt-1 line-clamp-2">{r.description}</p>
              )}
              <p className="mt-1 text-xs text-gray-500">
                {r.user}
              </p>
            </div>
          </div>

          {/* Acciones */}
          <div className="flex items-center gap-2 shrink-0">
            <motion.button
              type="button"
              onClick={() => downloadICS(r)}
              className="px-3 py-2 rounded-xl border border-gray-200 text-gray-700 hover:bg-gray-50"
              whileTap={{ scale: 0.98 }}
              title="Descargar .ics"
            >
              <CalendarDays className="w-4 h-4" />
            </motion.button>

            <motion.button
              type="button"
              onClick={() => onEdit?.(r)}
              className="px-3 py-2 rounded-xl border border-gray-200 text-gray-700 hover:bg-gray-50"
              whileTap={{ scale: 0.98 }}
              aria-label="Editar"
              title="Editar"
            >
              <Edit3 className="w-4 h-4" />
            </motion.button>

            <motion.button
              type="button"
              onClick={() => handleDelete(r.id)}
              className={`px-3 py-2 rounded-xl text-white font-medium shadow-sm ${
                deletingId === r.id
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-red-600 hover:bg-red-700'
              }`}
              whileTap={{ scale: deletingId === r.id ? 1 : 0.98 }}
              disabled={deletingId === r.id}
              aria-label="Eliminar"
              title="Eliminar"
            >
              {deletingId === r.id ? 'Eliminandoâ€¦' : <Trash2 className="w-4 h-4" />}
            </motion.button>

            {/* BotÃ³n Sincronizar (solo si estÃ¡ Local) */}
              {!outlook && (
                <motion.button
                  type="button"
                  onClick={async () => {
                    if (syncingId) return;
                    try {
                      setSyncingId(r.id);
                      const updated = await resyncReservationToOutlook(r);
                      // Notifica arriba para que persista el cambio (Context)
                      await onResync?.(updated);
                      // Si no pasas onResync, al menos alerta
                    } catch (e) {
                      alert(e?.message || "No se pudo sincronizar con Outlook.");
                    } finally {
                      setSyncingId(null);
                    }
                  }}
                  className={`px-3 py-2 rounded-xl border font-medium ${
                    syncingId === r.id
                      ? 'border-gray-200 text-gray-400 cursor-not-allowed'
                      : 'border-blue-200 text-blue-700 hover:bg-blue-50'
                  }`}
                  whileTap={{ scale: syncingId === r.id ? 1 : 0.98 }}
                  disabled={syncingId === r.id}
                  title="Sincronizar con Outlook"
                >
                  {syncingId === r.id ? 'Sincronizandoâ€¦' : (
                    <span className="inline-flex items-center gap-1">
                      <RotateCw className="w-4 h-4" /> Sync
                    </span>
                  )}
                </motion.button>
              )}
          </div>
        </div>
      </motion.li>
    );
  };

  if (!reservations.length) {
    return (
      <div className="text-center text-gray-500 bg-white/70 border border-gray-200/50 rounded-2xl p-10">
        No hay reservas para mostrar.
      </div>
    );
  }

  return (
    <ul className="space-y-3">
      {reservations.map((r) => (
        <Item key={r.id} r={r} />
      ))}
    </ul>
  );
};

export default ReservationList;
