import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu, Search } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';
import { useReservations } from '../context/ReservationsContext';

const AdminPanel = ({ sidebarOpen, toggleSidebar, currentPath }) => {
  const { reservations, addOrUpdate, removeById } = useReservations();
  const [searchTerm, setSearchTerm] = useState('');
  const [editing, setEditing] = useState(null); // reserva seleccionada para editar

  const filtered = reservations.filter(r => {
    const roomName = r.room?.nombre || '';
    return (
      r.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      r.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
      roomName.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />

      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Panel Admin</h2>
          <motion.button
            onClick={toggleSidebar}
            className="p-2 rounded-lg hover:bg-gray-100"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6" />
          </motion.button>
        </div>

        <div className="relative mb-6">
          <Search className="w-5 h-5 absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
          <input
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            placeholder="Buscar por tÃ­tulo, usuario o sala..."
            className="w-full pl-10 pr-4 py-2 rounded-xl border border-gray-200 outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <ReservationList
          reservations={filtered}
          onEdit={(r) => setEditing(r)}
          onDelete={(id) => removeById(id)}
          isAdmin={true}
        />

        <p className="text-sm text-gray-500 mt-4 text-center">
          Total de reservas: {reservations.length} | Filtradas: {filtered.length}
        </p>

        {editing && (
          <div className="mt-8">
            <ReservationForm
              userName={editing.user}       // mantiene el usuario original al editar
              reservationToEdit={editing}
              allReservations={reservations}
              onUpsert={(res) => {
                addOrUpdate(res);
                setEditing(null);
              }}
            />
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default AdminPanel;
