import React, { useMemo, useState } from 'react';
import ConfirmationModal from './ui/ConfirmationModal';
import { Loader2 } from 'lucide-react';

export default function ReservationForm({
  onCreate,
  onViewMyReservations,
  defaultRoomId,
  defaultDate,
  rooms = [] // opcional para mostrar nombres
}) {
  const [title, setTitle] = useState('');
  const [roomId, setRoomId] = useState(defaultRoomId || rooms[0]?.id || '');
  const [date, setDate] = useState(defaultDate || new Date());
  const [start, setStart] = useState('09:00');
  const [end, setEnd] = useState('10:00');
  const [description, setDescription] = useState('');
  const [guests, setGuests] = useState('');
  const [location, setLocation] = useState('');

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [confirmOpen, setConfirmOpen] = useState(false);
  const [confirmData, setConfirmData] = useState(null);

  const startISO = useMemo(() => {
    const d = new Date(date);
    const [h, m] = start.split(':').map(Number);
    d.setHours(h, m, 0, 0);
    return d.toISOString();
  }, [date, start]);

  const endISO = useMemo(() => {
    const d = new Date(date);
    const [h, m] = end.split(':').map(Number);
    d.setHours(h, m, 0, 0);
    return d.toISOString();
  }, [date, end]);

  function getRoomName(id) {
    return rooms.find(r => r.id === id)?.name || id;
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const created = await onCreate({
        roomId,
        title,
        start: startISO,
        end: endISO,
        description,
        guests: guests.split(',').map(s => s.trim()).filter(Boolean),
        location
      });

      setConfirmData({
        title: created.title || title,
        roomName: created.roomName || getRoomName(created.roomId || roomId),
        start: created.start || startISO,
        end: created.end || endISO
      });
      setConfirmOpen(true);

      // (opcional) reset selectivo
      // setTitle(''); setDescription(''); setGuests('');
    } catch (err) {
      setError(err?.message || 'No se pudo crear la reserva.');
    } finally {
      setLoading(false);
    }
  }

  const fmtDate = (d) => d.toLocaleDateString(undefined, { year: 'numeric', month: 'short', day: '2-digit' });

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Título */}
      <div>
        <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Título</label>
        <input
          value={title}
          onChange={(e)=>setTitle(e.target.value)}
          className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm"
          placeholder="Reunión de proyecto"
        />
      </div>

      {/* Sala */}
      <div>
        <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Sala</label>
        <select
          value={roomId}
          onChange={(e)=>setRoomId(e.target.value)}
          className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm"
        >
          {rooms.length ? rooms.map(r => (
            <option key={r.id} value={r.id}>{r.name} · {r.capacity ?? '—'}p</option>
          )) : <option value={roomId}>{roomId || 'Selecciona...'}</option>}
        </select>
      </div>

      {/* Fecha + Horas */}
      <div className="grid grid-cols-3 gap-3">
        <div>
          <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Fecha</label>
          {/* Usa tu DatePicker propio; aquí muestro un input ilustrativo */}
          <input
            type="date"
            value={new Date(date).toISOString().slice(0,10)}
            onChange={(e)=>setDate(new Date(e.target.value + 'T00:00:00'))}
            className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm"
          />
          <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">{fmtDate(date)}</p>
        </div>
        <div>
          <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Hora inicio</label>
          <input type="time" value={start} onChange={(e)=>setStart(e.target.value)}
                 className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm" />
        </div>
        <div>
          <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Hora fin</label>
          <input type="time" value={end} onChange={(e)=>setEnd(e.target.value)}
                 className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm" />
        </div>
      </div>

      {/* Extras */}
      <div className="grid grid-cols-2 gap-3">
        <div>
          <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Invitados (coma)</label>
          <input value={guests} onChange={(e)=>setGuests(e.target.value)}
                 className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm" />
        </div>
        <div>
          <label className="block text-xs font-medium text-gray-600 dark:text-gray-300 mb-1">Ubicación (opcional)</label>
          <input value={location} onChange={(e)=>setLocation(e.target.value)}
                 className="w-full rounded-xl border border-gray-200 dark:border-gray-800 bg-white/80 dark:bg-white/5 px-3 py-2 text-sm" />
        </div>
      </div>

      {error && <p className="text-sm text-red-600 dark:text-red-400">{error}</p>}

      <div className="flex justify-end">
        <button
          type="submit"
          disabled={loading}
          className="inline-flex items-center gap-2 rounded-xl px-4 py-2 font-medium text-white bg-blue-600 hover:bg-blue-500 disabled:opacity-60 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          {loading ? (<><Loader2 className="w-4 h-4 animate-spin" /> Creando…</>) : 'Crear reserva'}
        </button>
      </div>

      <ConfirmationModal
        open={confirmOpen}
        onClose={() => setConfirmOpen(false)}
        data={confirmData || {}}
        onCTA={{
          primary: () => {
            setConfirmOpen(false);
            if (onViewMyReservations) onViewMyReservations();
            else window.location.href = '/my-reservations';
          }
        }}
      />
    </form>
  );
}
