// src/components/DatePickerField.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import { createPortal } from 'react-dom';
import { DayPicker } from 'react-day-picker';
import { format, parse, isValid, addDays } from 'date-fns';
import { es } from 'date-fns/locale';
import { CalendarDays, X, ChevronLeft, ChevronRight } from 'lucide-react';

const IconLeft  = () => <ChevronLeft  className="w-4 h-4" />;
const IconRight = () => <ChevronRight className="w-4 h-4" />;

const FMT = 'yyyy-MM-dd';
const parseISO = (s) => {
  const d = parse(s, FMT, new Date());
  return isValid(d) ? d : undefined;
};

// üîß CSS duro: fuerza layout de tabla y tama√±os aunque haya resets globales
const RDP_HARD_FIX = `
/* ‚Äî‚Äî‚Äî Layout de tabla (a prueba de resets) ‚Äî‚Äî‚Äî */
.rdp table{ width:100% !important; border-collapse:collapse !important; table-layout:fixed !important; }
.rdp thead, .rdp tbody { display: table-header-group !important; }
.rdp tr { display: table-row !important; }
.rdp th, .rdp td { display: table-cell !important; padding:0 !important; text-align:center !important; vertical-align:middle !important; }

/* ‚Äî‚Äî‚Äî Tama√±o de d√≠a ‚Äî‚Äî‚Äî */
.rdp .rdp-day{
  width:2.25rem !important; height:2.25rem !important;
  display:grid !important; place-items:center !important;
  margin:0 auto !important; border-radius:.75rem !important; cursor:pointer;
}

/* ‚Äî‚Äî‚Äî Paleta LIGHT ‚Äî‚Äî‚Äî */
.rdp{ color:#111827 !important; }                     /* gray-900 */
.rdp .rdp-head_cell{ color:#6B7280 !important; }      /* gray-500 */
.rdp .rdp-day_outside{ color:#D1D5DB !important; }    /* gray-300 */
.rdp .rdp-day:hover{ background:rgba(0,0,0,.04); }

/* ‚Äî‚Äî‚Äî Paleta DARK forzada (alto contraste) ‚Äî‚Äî‚Äî */
.dark .rdp{ color:#E5E7EB !important; }               /* gray-200 */
.dark .rdp .rdp-caption_label{ color:#F9FAFB !important; } /* casi blanco */
.dark .rdp .rdp-head_cell{ color:rgba(255,255,255,.65) !important; }
.dark .rdp .rdp-day{ color:#E5E7EB !important; }
.dark .rdp .rdp-day_outside{ color:rgba(255,255,255,.38) !important; }
.dark .rdp .rdp-day:hover{ background:rgba(255,255,255,.07); }

/* Asegura que nada baje la opacidad global */
.dark .rdp, .dark .rdp * { opacity: 1 !important; }

/* Jerarqu√≠a */
.rdp .rdp-caption_label{ font-weight:700 !important; }
.rdp .rdp-head_cell{ font-weight:600 !important; height:2.25rem !important; }
`;


export default function DatePickerField({
  value, onChange,
  placeholder = 'dd/mm/aaaa',
  id, name = 'date', required, disabled,
  mode = 'auto',
}) {
  const [open, setOpen] = useState(false);
  const btnRef = useRef(null);
  const popRef = useRef(null);

  const isDesktop = useMemo(() => {
    if (mode === 'native') return false;
    if (mode === 'custom') return true;
    if (typeof window === 'undefined' || !window.matchMedia) return true;
    const m1 = window.matchMedia('(hover: hover) and (pointer: fine)').matches;
    const m2 = window.innerWidth >= 768;
    return m1 && m2;
  }, [mode]);

  useEffect(() => {
    const onDown = (e) => {
      if (!open) return;
      if (popRef.current?.contains(e.target)) return;
      if (btnRef.current?.contains(e.target)) return;
      setOpen(false);
    };
    const onKey = (e) => { if (e.key === 'Escape') setOpen(false); };
    document.addEventListener('mousedown', onDown);
    document.addEventListener('keydown', onKey);
    return () => {
      document.removeEventListener('mousedown', onDown);
      document.removeEventListener('keydown', onKey);
    };
  }, [open]);

  const selected = value ? parseISO(value) : undefined;

  // M√≥vil: input nativo
  if (!isDesktop) {
    return (
      <div className="relative">
        <input
          id={id} name={name} type="date" required={required} disabled={disabled}
          value={value || ''} onChange={(e) => onChange?.(e.target.value)}
          className="mt-1.5 w-full h-11 rounded-xl px-3.5
                     bg-white dark:bg-[#0F1318]
                     border border-gray-300 dark:border-[#2A3342]
                     text-gray-900 dark:text-gray-100
                     placeholder:text-gray-400 dark:placeholder:text-gray-500
                     focus:outline-none focus:ring-2 focus:ring-blue-500/30"
          placeholder={placeholder}
        />
        <CalendarDays className="w-4 h-4 absolute right-3 top-1/2 -translate-y-1/2 opacity-70 pointer-events-none" />
      </div>
    );
  }

  // Desktop
  const btnLabel = value ? format(parseISO(value), 'dd/MM/yyyy') : placeholder;

  // Popover centrado respecto al input
  const [pos, setPos] = useState({ top: 0, left: 0, minWidth: 320 });
  useEffect(() => {
    if (!open || !btnRef.current) return;
    const r = btnRef.current.getBoundingClientRect();
    const gap = 8;
    const width = Math.max(320, r.width);
    let left = r.left + (r.width - width) / 2;
    left = Math.max(8, Math.min(window.innerWidth - width - 8, left));
    const top = r.bottom + gap + window.scrollY;
    setPos({ top, left: left + window.scrollX, minWidth: width });
  }, [open]);

  const Popover = (
    <div
      ref={popRef}
      role="dialog"
      style={{ position: 'absolute', top: pos.top, left: pos.left, minWidth: pos.minWidth }}
      className="rdp-pop z-[1000] rounded-2xl border border-gray-200/70 dark:border-[#374151] bg-white dark:bg-[#111827] shadow-xl p-3 max-h-[420px] overflow-auto">
      {/* Atajos */}
      <div className="flex items-center justify-between px-2 pb-1">
        <div className="flex items-center gap-2">
          <button
            className="text-xs px-2 py-1 rounded-md border border-gray-200 dark:border-[#2A3342]
                       hover:bg-gray-100 dark:hover:bg-[#141922] text-gray-700 dark:text-gray-200"
            onClick={() => { onChange?.(format(new Date(), FMT)); setOpen(false); }}
          >
            Hoy
          </button>
          <button
            className="text-xs px-2 py-1 rounded-md border border-gray-200 dark:border-[#2A3342]
                       hover:bg-gray-100 dark:hover:bg-[#141922] text-gray-700 dark:text-gray-200"
            onClick={() => { onChange?.(format(addDays(new Date(), 1), FMT)); setOpen(false); }}
          >
            Ma√±ana
          </button>
          <button
            className="text-xs px-2 py-1 rounded-md border border-gray-200 dark:border-[#2A3342]
                       hover:bg-gray-100 dark:hover:bg-[#141922] text-gray-600 dark:text-gray-300"
            onClick={() => { onChange?.(''); setOpen(false); }}
          >
            Limpiar
          </button>
        </div>
        <button
          onClick={() => setOpen(false)}
          className="p-1 rounded-md hover:bg-gray-100 dark:hover:bg-[#141922] text-gray-500"
          aria-label="Cerrar calendario"
        >
          <X className="w-4 h-4" />
        </button>
      </div>

      {/* Fix de layout inyectado al final del DOM del popover */}
     <style dangerouslySetInnerHTML={{
            __html: `
        /* ‚Äî‚Äî Resetea cualquier opacidad/filtro heredado en el popover ‚Äî‚Äî */
        .rdp-pop{
        opacity: 1 !important;
        filter: none !important;
        -webkit-filter: none !important;
        mix-blend-mode: normal !important;
        color-scheme: dark;
        isolation: isolate;            /* evita que filtros del padre afecten a hijos */
        }
        .rdp-pop *{
        mix-blend-mode: normal !important;
        }
        /* si el padre aplica variables de Tailwind que aten√∫an texto, neutral√≠zalas */
        .rdp-pop{ --tw-text-opacity: 1 !important; }
        `
        }}
        />


      <DayPicker
        captionLayout="dropdown-buttons"
        fromYear={2020}
        toYear={2032}
        components={{ IconLeft, IconRight }}

        mode="single"
        locale={es}
        selected={selected}
        onSelect={(d) => { if (d) { onChange?.(format(d, FMT)); setOpen(false); } }}
        showOutsideDays
        weekStartsOn={1}
        disabled={{ before: new Date() }}

        /* Weekdays en 2 letras (LU, MA, ‚Ä¶) */
        formatters={{
          formatWeekdayName: (day) =>
            new Intl.DateTimeFormat('es-ES', { weekday: 'short' })
              .format(day).slice(0,2).toUpperCase(),
        }}

        /* üëá NO styles={{ head_row/month_grid/... }} aqu√≠. Dejamos que la TABLA haga su trabajo */
        classNames={{
            /* caption + nav */
            caption_label:
                'text-sm font-semibold text-gray-900 dark:text-gray-100 tracking-tight',
            nav: 'flex items-center',
            button_previous:
                'p-2 rounded-lg text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-white/10 focus:outline-none focus:ring-2 focus:ring-blue-500/30',
            button_next:
                'p-2 rounded-lg text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-white/10 focus:outline-none focus:ring-2 focus:ring-blue-500/30',

            /* weekdays */
            head: 'mt-1',
            head_row: 'text-xs text-gray-500 dark:text-gray-400',
            head_cell: 'font-medium',

            /* grid */
            month: 'space-y-2',

            /* days */
            day:
                'transition-colors hover:bg-gray-100 dark:hover:bg-white/5 focus:outline-none focus:ring-2 focus:ring-blue-500/30',

            day_selected:
                'bg-blue-600 text-white hover:bg-blue-600 focus:bg-blue-600 shadow-sm ' +
                'dark:bg-blue-600 dark:text-white dark:ring-0 dark:hover:bg-blue-600',

            day_today:
                'relative after:content-[""] after:absolute after:bottom-1 after:w-1.5 after:h-1.5 after:rounded-full after:bg-blue-500 ' +
                'dark:after:bg-blue-400',

            /* Outside/disabled atenuados en dark */
            day_outside: 'text-gray-300 dark:text-gray-500',
            day_disabled: 'opacity-40 cursor-not-allowed',
            }}
      />
    </div>
  );

  return (
    <>
      <button
        ref={btnRef}
        type="button"
        id={id}
        className="mt-1.5 w-full h-11 rounded-xl px-3.5 text-left
                   bg-white dark:bg-[#0F1318]
                   border border-gray-300 dark:border-[#2A3342]
                   text-gray-900 dark:text-gray-100
                   focus:outline-none focus:ring-2 focus:ring-blue-500/30
                   flex items-center justify-between"
        onClick={() => setOpen(v => !v)}
        aria-haspopup="dialog"
        aria-expanded={open}
      >
        <span className={value ? '' : 'text-gray-400 dark:text-gray-500'}>
          {btnLabel}
        </span>
        <CalendarDays className="w-4 h-4 opacity-80" />
      </button>

      <input type="hidden" name={name} value={value || ''} required={required} disabled={disabled} />
      {open && createPortal(Popover, document.body)}
    </>
  );
}
