import { useEffect, useState } from 'react';
import { apiDeleteReservation } from '../api/reservations';
import { cacheList } from '../utils/reservationsCache';

export default function MyReservations({ accessToken }) {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [busyId, setBusyId] = useState(null);
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    let alive = true;

    async function tryJSON(url) {
      const res = await fetch(url, {
        headers: {
          Accept: 'application/json',
          ...(accessToken ? { Authorization: `Bearer ${accessToken}` } : {}),
        },
      });
      const ct = res.headers.get('content-type') || '';
      const text = await res.text();
      if (!res.ok) throw new Error(`${res.status}`);
      if (!ct.includes('application/json')) throw new Error(`non-json`);
      const data = JSON.parse(text);
      if (!Array.isArray(data)) throw new Error('not-array');
      return data;
    }

    (async () => {
      setLoading(true);
      try {
        const base = import.meta?.env?.VITE_API_BASE_URL || ''; // ej: http://localhost:4000
        const candidates = [
          `${base}/api/reservations/mine`,
          `${base}/api/reservations?mine=true`,
          `${base}/api/my-reservations`,
          `${base}/api/reservations`, // si el backend filtra por el usuario autenticado
        ];

        let data = null, lastErr = null;
        for (const url of candidates) {
          try {
            data = await tryJSON(url);
            break;
          } catch (e) {
            lastErr = e;
          }
        }
        if (!data) throw new Error(`No encontré un endpoint válido (${lastErr?.message || 'desconocido'}).`);

        if (alive) setReservations(data);
      } catch (e) {
        if (alive) setError(
          `No se pudieron cargar tus reservas. Verifica la ruta/proxy. ${e?.message ? 'Detalle: ' + e.message : ''}`
        );
      } finally {
        if (alive) setLoading(false);
      }
    })();

    return () => { alive = false; };
  }, [accessToken]);


  async function handleDelete(r) {
    const id = r.id || r._id;
    if (!id) return;
    if (!window.confirm(`¿Eliminar la reserva “${r.title || 'sin título'}”?`)) return;

    setBusyId(id);
    setMsg('');
    const prev = reservations;
    setReservations(prev => prev.filter(x => (x.id || x._id) !== id)); // optimista

    try {
      await apiDeleteReservation(id, accessToken);
      setMsg('Reserva eliminada correctamente.');
    } catch (err) {
      setReservations(prev); // revertir
      setMsg(err?.message || 'No se pudo eliminar la reserva.');
    } finally {
      setBusyId(null);
      setTimeout(() => setMsg(''), 2500);
    }
  }

  const hhmm = (d) => d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  return (
    <>
      <div className="sr-only" aria-live="polite">
        {msg || (loading ? 'Cargando reservas…' : '')}
      </div>

      {error && (
        <div className="mx-auto max-w-4xl mt-6 rounded-xl border border-red-200 dark:border-red-900/40 bg-red-50/70 dark:bg-red-900/20 px-4 py-3 text-sm text-red-800 dark:text-red-200">
          {error}
        </div>
      )}

      {loading && !error && (
        <div className="mx-auto max-w-4xl mt-6 text-sm text-gray-600 dark:text-gray-300">
          Cargando tus reservas…
        </div>
      )}

      {!loading && !error && reservations.length === 0 && (
        <div className="mx-auto max-w-4xl mt-10 rounded-2xl border border-gray-200 dark:border-[#232A36] bg-white dark:bg-[#171B22] p-6 text-center">
          <p className="text-gray-700 dark:text-gray-200 font-medium">Aún no tienes reservas.</p>
          <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">Crea tu primera desde “Crear Reserva”.</p>
        </div>
      )}

      {!loading && !error && reservations.length > 0 && (
        <ul className="mx-auto max-w-4xl mt-4 space-y-3">
          {reservations
            .sort((a, b) => new Date(a.startTime || a.start) - new Date(b.startTime || b.start))
            .map((r) => {
              const id = r.id || r._id;
              const start = new Date(r.startTime || r.start);
              const end = new Date(r.endTime || r.end);
              const dateStr = start.toLocaleDateString([], { day: '2-digit', month: 'short', year: 'numeric' });

              return (
                <li
                  key={id}
                  className="rounded-xl bg-white dark:bg-[#171B22] border border-gray-200 dark:border-[#232A36] px-4 py-3 flex items-start justify-between"
                >
                  <div>
                    <div className="font-semibold text-gray-900 dark:text-white">{r.title || 'Reserva'}</div>
                    <div className="text-sm text-gray-600 dark:text-gray-300">
                      {(r.room?.nombre || r.room?.name || r.roomId || 'Sala')} · {dateStr}, {hhmm(start)}–{hhmm(end)}
                    </div>
                  </div>
                  <button
                    onClick={() => handleDelete(r)}
                    disabled={busyId === id}
                    className="rounded-lg px-3 py-1.5 text-sm border border-gray-200 dark:border-[#2A3342] hover:bg-gray-50 dark:hover:bg-[#141922] disabled:opacity-60 disabled:cursor-not-allowed"
                  >
                    {busyId === id ? 'Eliminando…' : 'Eliminar'}
                  </button>
                </li>
              );
            })}
        </ul>
      )}

      {msg && (
        <div className="fixed bottom-4 right-4 z-50 rounded-xl border px-4 py-2 text-sm bg-white/95 dark:bg-gray-900/95 border-gray-200 dark:border-gray-800 shadow-2xl">
          {msg}
        </div>
      )}
    </>
  );
}
