import React, { useEffect, useState } from 'react';
import { Sun, Moon, Laptop } from 'lucide-react';
import { getSavedTheme, setTheme } from '../utils/theme';
import { motion } from 'framer-motion';

const options = [
  { key: 'light', label: 'Claro', Icon: Sun },
  { key: 'dark', label: 'Oscuro', Icon: Moon },
  { key: 'system', label: 'Sistema', Icon: Laptop },
];

const ThemeToggle = () => {
  const [pref, setPref] = useState('system');

  useEffect(() => {
    setPref(getSavedTheme());
  }, []);

  const cycle = () => {
    const order = ['light', 'dark', 'system'];
    const next = order[(order.indexOf(pref) + 1) % order.length];
    setPref(next);
    setTheme(next);
  };

  const CurrentIcon = options.find(o => o.key === pref)?.Icon ?? Laptop;
  const title = `Tema: ${options.find(o => o.key === pref)?.label || 'Sistema'}`;

  return (
    <motion.button
      onClick={cycle}
      title={title}
      aria-label={title}
      className="inline-flex items-center gap-2 px-3 py-2 rounded-xl border border-gray-200 text-gray-700 hover:bg-gray-50
                 dark:border-gray-700 dark:text-gray-200 dark:hover:bg-white/10 transition-colors"
      whileTap={{ scale: 0.96 }}
    >
      <CurrentIcon className="w-4 h-4" />
      <span className="hidden sm:inline">{options.find(o => o.key === pref)?.label}</span>
    </motion.button>
  );
};

export default ThemeToggle;
