// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import Header from './components/Header';
import CreateReservation from './pages/CreateReservation';
import MyReservations from './pages/MyReservations';
import AdminPanel from './pages/AdminPanel';
import { ReservationsProvider } from './context/ReservationsContext';
import { msalInstance } from './msalConfig';
import { EventType } from '@azure/msal-browser';
import { useDarkMode } from './hooks/useDarkMode'; // ðŸ‘ˆ NUEVO

const AppContent = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { isDark, toggle } = useDarkMode(); // ðŸ‘ˆ NUEVO

  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [reservationToEdit, setReservationToEdit] = useState(null);

  const [msalTick, setMsalTick] = useState(0);
  useEffect(() => {
    const cbId = msalInstance.addEventCallback((event) => {
      if (
        event.eventType === EventType.LOGIN_SUCCESS ||
        event.eventType === EventType.ACQUIRE_TOKEN_SUCCESS ||
        event.eventType === EventType.HANDLE_REDIRECT_END ||
        event.eventType === EventType.LOGOUT_SUCCESS
      ) {
        setMsalTick((t) => t + 1);
      }
    });
    return () => { if (cbId) msalInstance.removeEventCallback(cbId); };
  }, []);

  const account = msalInstance.getActiveAccount() || msalInstance.getAllAccounts()[0] || null;
  const userName = account?.username || 'Invitado';

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const currentPath = location.pathname;

  const handleEditReservation = (reservation) => {
    setReservationToEdit(reservation);
    navigate('/');
  };

  return (
    // ðŸ‘‡ Clases base para ambos temas (aplican a TODAS las vistas)
    <div className="flex flex-col h-screen bg-white text-gray-900 dark:bg-dark-bg dark:text-dark-text-primary">
      {/* PÃ¡sale el toggle al Header si quieres un switch allÃ­ */}
      <Header currentPath={currentPath} isDark={isDark} onToggleTheme={toggle} />

      <Routes>
        <Route
          path="/"
          element={
            <CreateReservation
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              reservationToEdit={reservationToEdit}
              currentPath={currentPath}
            />
          }
        />
        <Route
          path="/my-reservations"
          element={
            <MyReservations
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={currentPath}
            />
          }
        />
        <Route
          path="/admin"
          element={
            <AdminPanel
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={currentPath}
              onEdit={handleEditReservation}
            />
          }
        />
      </Routes>
    </div>
  );
};

const App = () => (
  <Router>
    <ReservationsProvider>
      <AppContent />
    </ReservationsProvider>
  </Router>
);

export default App;
