import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import { useReservations } from '../context/ReservationsContext';
import { useParams } from 'react-router-dom'; //Para leer el ID de la URL

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  currentPath,
}) => {
  const { id } = useParams(); //ID de la URL (ej: 'XYZ123')
  const { reservations, addOrUpdate } = useReservations(); // 1. OBTENER LA LISTA Y LA FUNCIÓN DE GUARDADO DEL CONTEXTO
  const reservationToEdit = reservations.find(r => r.id === id); // 2. BUSCAR LA RESERVA A EDITAR

  // 3. (Opcional) Mostrar un mensaje si no se encuentra o está cargando
  if (id && !reservationToEdit && reservations.length > 0) {
      // return <div>Buscando reserva para editar...</div>;
  }
  // 4. PASAR EL OBJETO ENCONTRADO AL FORMULARIO
return (
    // ...
      <ReservationForm
        userName={userName}
        reservationToEdit={reservationToEdit} 
        onUpsert={addOrUpdate}
      />
  );
};

export default CreateReservation;
