import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu, Search, RefreshCw } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';
import { useReservations } from '../context/ReservationsContext';
import { deleteReservationInOutlookIfAny } from '../utils/deleteReservation'; // ðŸ‘ˆ NUEVO
import { initialReservations } from '../utils/reservations'; // ðŸ‘ˆ para el reset opcional

const AdminPanel = ({ sidebarOpen, toggleSidebar, currentPath }) => {
  const { reservations, addOrUpdate, removeById } = useReservations();
  const [searchTerm, setSearchTerm] = useState('');
  const [editing, setEditing] = useState(null); // reserva seleccionada para editar
  const [resetInfo, setResetInfo] = useState('');
  const [filterRoom, setFilterRoom] = useState('all');
  const [filterDate, setFilterDate] = useState(''); // yyyy-MM-dd


  const filtered = reservations.filter(r => {
    const roomName = r.room?.nombre || '';
    return (
      r.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      r.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
      roomName.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const handleDelete = async (id) => {
    const target = reservations.find(r => r.id === id);
    if (target) {
      await deleteReservationInOutlookIfAny(target); // ðŸ‘ˆ intenta borrar en Outlook
    }
    removeById(id); // ðŸ‘ˆ luego elimina localmente
  };

  // --- OPCIONAL: Resetear localStorage con la semilla ---
  const handleResetReservations = async () => {
    try {
      // Ojo: esto NO borra eventos en Outlook, solo resetea localmente.
      localStorage.removeItem('reservations');
      const seeded = initialReservations.map(r => ({
        ...r,
        // asegurar ISO
        startTime: new Date(r.startTime).toISOString(),
        endTime: new Date(r.endTime).toISOString(),
      }));
      localStorage.setItem('reservations', JSON.stringify(seeded));
      setResetInfo('Reservas reseteadas localmente. Recarga la pÃ¡gina para ver los cambios.');
    } catch (e) {
      setResetInfo('No se pudo resetear. Revisa permisos del navegador.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />

      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Panel Admin</h2>
          <motion.button
            onClick={toggleSidebar}
            className="p-2 rounded-lg hover:bg-gray-100"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6" />
          </motion.button>
        </div>

        <div className="flex items-center gap-3 mb-6">
          <div className="relative flex-1">
            <Search className="w-5 h-5 absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
            <input
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              placeholder="Buscar por tÃ­tulo, usuario o sala..."
              className="w-full pl-10 pr-4 py-2 rounded-xl border border-gray-200 outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="flex flex-col md:flex-row gap-3 mb-6">
            <div className="relative flex-1">
              {/* tu input de bÃºsqueda existente */}
            </div>
            <select
              value={filterRoom}
              onChange={(e) => setFilterRoom(e.target.value)}
              className="px-3 py-2 rounded-xl border border-gray-200"
              title="Filtrar por sala"
            >
              <option value="all">Todas las salas</option>
              <option value="1">CÃ­rculo de Sabios I</option>
              <option value="2">CÃ­rculo de Sabios II</option>
              <option value="3">CÃ­rculo de Sabios III</option>
              <option value="4">Consejo de la Tribu</option>
              <option value="5">Plaza de Conexiones</option>
            </select>
            <input
              type="date"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
              className="px-3 py-2 rounded-xl border border-gray-200"
              title="Filtrar por fecha"
            />
          </div>


          {/* BotÃ³n OPCIONAL: reset local */}
          <motion.button
            type="button"
            onClick={handleResetReservations}
            className="hidden md:inline-flex items-center gap-2 px-3 py-2 rounded-xl border border-gray-200 text-gray-700 hover:bg-gray-50"
            title="Resetear reservas locales (solo para pruebas)"
            whileTap={{ scale: 0.98 }}
          >
            <RefreshCw className="w-4 h-4" />
            Reset
          </motion.button>
        </div>

        {resetInfo && (
          <div className="mb-4 text-sm text-blue-700 bg-blue-50 border border-blue-200 rounded-xl p-3">
            {resetInfo}
          </div>
        )}

        <ReservationList
          reservations={filtered}
          onEdit={(r) => setEditing(r)}
          onDelete={handleDelete}
          isAdmin={true}
        />
        {/* ðŸ‘‰ ahora borra Outlook + local */}

        <p className="text-sm text-gray-500 mt-4 text-center">
          Total de reservas: {reservations.length} | Filtradas: {filtered.length}
        </p>

        {editing && (
          <div className="mt-8">
            <ReservationForm
              userName={editing.user}       // conserva el usuario original
              reservationToEdit={editing}
              allReservations={reservations}
              onUpsert={(res) => {
                addOrUpdate(res);
                setEditing(null);
              }}
            />
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default AdminPanel;
