import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';

const CreateReservation = ({ reservations, onAdd, userName, sidebarOpen, toggleSidebar }) => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="md:hidden mb-4">
          <motion.button
            onClick={toggleSidebar}
            className="p-3 bg-white rounded-xl shadow-sm"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6 text-gray-700" />
          </motion.button>
        </div>
        <ReservationForm onAddReservation={onAdd} userName={userName} />
        <OutlookSync />
      </motion.div>
    </div>
  );
};

export default CreateReservation;
