import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { useReservations } from '../context/ReservationsContext';
import ReservationForm from '../components/ReservationForm';
import { msalInstance, loginRequest } from '../msalConfig'; //  IMPORTANTE: Importar MSAL

const CreateReservation = ({
  userName, // Esta prop viene de App.js, si es null/undefined, no est谩s logueado
  sidebarOpen,
  toggleSidebar,
  currentPath,
}) => {
  const { id } = useParams();
  const location = useLocation();
  const { reservations, addOrUpdate } = useReservations();

  // 1. ESTADO PARA LA RESERVA A EDITAR
  const [reservationToEdit, setReservationToEdit] = useState(undefined);

  // 2. VERIFICACIN DE SESIN LOCAL
  const [isAuthenticated, setIsAuthenticated] = useState(!!msalInstance.getActiveAccount());

  // Funci贸n para manejar el Login Manual
  const handleLogin = async () => {
    try {
      await msalInstance.loginPopup(loginRequest);
      // Una vez logueado, verificamos y recargamos para que el Contexto tome el token
      const account = msalInstance.getActiveAccount();
      if (account) {
        setIsAuthenticated(true);
        window.location.reload(); // Recarga simple para asegurar que todo el Contexto se reinicie limpio
      }
    } catch (error) {
      console.error("Error al iniciar sesi贸n:", error);
    }
  };

  // 3. LGICA DE BSQUEDA REACTIVA (Para Edici贸n)
  useEffect(() => {
    const isEditing = !!id;
    if (isEditing && reservations.length > 0) {
      // Comparaci贸n segura de IDs (string vs number)
      const foundReservation = reservations.find(r => r.id == id);
      setReservationToEdit(foundReservation);
    }
  }, [reservations, id]);

  // ---------------------------------------------------------------
  // PANTALLA 1: SI NO HAY SESIN, MOSTRAR BOTN DE LOGIN
  // ---------------------------------------------------------------
  if (!isAuthenticated) {
    return (
      <div className="flex flex-col items-center justify-center h-[60vh] text-center p-6">
        <div className="bg-blue-50 text-blue-800 p-4 rounded-full mb-4">
          <svg className="w-12 h-12" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
          </svg>
        </div>
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Inicia sesi贸n para continuar</h2>
        <p className="text-gray-600 mb-6 max-w-md">
          Necesitas conectar tu cuenta de Outlook para ver la disponibilidad de salas y gestionar tus reservas.
        </p>
        <button
          onClick={handleLogin}
          className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transition-all transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          Iniciar Sesi贸n con Microsoft
        </button>
      </div>
    );
  }

  // ---------------------------------------------------------------
  // PANTALLA 2: MODO EDICIN CARGANDO
  // ---------------------------------------------------------------
  const isEditing = !!id;
  if (isEditing && !reservationToEdit) {
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center">
        <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mb-2"></div>
        <p className="text-gray-600">Cargando datos de la reserva...</p>
      </div>
    );
  }

  // ---------------------------------------------------------------
  // PANTALLA 3: FORMULARIO (Crear o Editar)
  // ---------------------------------------------------------------
  return (
    <ReservationForm
      // Usamos el ID como key para forzar reinicio si cambia la URL
      key={id || 'new-reservation'}
      userName={userName}
      reservationToEdit={reservationToEdit}
      onUpsert={addOrUpdate}
      // Agregamos onCancel para volver a la lista si se desea
      onCancel={() => window.location.href = '/my-reservations'} 
    />
  );
};

export default CreateReservation;
