import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import Header from './components/Header';
import CreateReservation from './pages/CreateReservation';
import MyReservations from './pages/MyReservations';
import AdminPanel from './pages/AdminPanel';
import { initialReservations, generateId } from './utils/reservations';

const AppContent = () => {
  const location = useLocation();
  const [reservations, setReservations] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const userName = 'Usuario Actual'; // Simular usuario logueado

  useEffect(() => {
    setReservations(initialReservations);
  }, []);

  const handleAddReservation = (newReservation, action = 'add') => {
    if (action === 'edit') {
      setReservations(prev => prev.map(r => r.id === newReservation.id ? newReservation : r));
    } else {
      setReservations(prev => [newReservation, ...prev]);
    }
  };

  const handleEditReservation = (reservation) => {
    window.location.href = '/'; // Redirigir a crear para editar
    // En producción, pasar datos via state o context
  };

  const handleDeleteReservation = (id) => {
    if (window.confirm('¿Estás seguro de eliminar esta reserva?')) {
      setReservations(prev => prev.filter(r => r.id !== id));
    }
  };

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div className="flex flex-col h-screen">
      <Header currentPath={location.pathname} />
      <Routes>
        <Route
          path="/"
          element={
            <CreateReservation
              reservations={reservations}
              onAdd={handleAddReservation}
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
            />
          }
        />
        <Route
          path="/my-reservations"
          element={
            <MyReservations
              reservations={reservations}
              onAdd={handleAddReservation}
              onEdit={handleEditReservation}
              onDelete={handleDeleteReservation}
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={location.pathname}
            />
          }
        />
        <Route
          path="/admin"
          element={
            <AdminPanel
              reservations={reservations}
              onEdit={handleEditReservation}
              onDelete={handleDeleteReservation}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={location.pathname}
            />
          }
        />
      </Routes>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <AppContent />
    </Router>
  );
};

export default App;
