import React from 'react';
import { motion } from 'framer-motion';
import { Home, Calendar, Settings, User } from 'lucide-react';
import { useLocation } from 'react-router-dom';
import ThemeToggle from './ThemeToggle'; // ðŸ‘ˆ nuevo import


const Header = ({ currentPath: propPath }) => {
  // Fallback robusto: usa el prop si viene; si no, toma la ruta actual
  const location = useLocation();
  const currentPath = propPath ?? location.pathname;

  const navItems = [
    { path: '/', icon: Home, label: 'Crear Reserva' },
    { path: '/my-reservations', icon: User, label: 'Mis Reservas' },
    { path: '/admin', icon: Settings, label: 'Admin' }
  ];

  return (
    <motion.header
      className="bg-white/80 dark:bg-white/5 backdrop-blur-xl border-b border-gray-200/50 dark:border-gray-800/50 shadow-lg transition-colors"
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="container mx-auto px-4 py-4 max-w-6xl">
        <div className="flex items-center justify-between">
          <motion.div className="flex items-center gap-3" whileHover={{ scale: 1.05 }}>
            <Calendar className="w-8 h-8 text-blue-600" />
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Salas FYCO
            </h1>
          </motion.div>

<div className="flex items-center gap-3">
  <ThemeToggle />
  <nav className="hidden md:flex items-center gap-8">
    {navItems.map((item) => {
      const Icon = item.icon;
      const isActive = currentPath === item.path;
      return (
        <motion.a
          key={item.path}
          href={item.path}
          className={`flex items-center gap-2 px-4 py-2 rounded-xl font-medium transition-all duration-300 ${
            isActive
              ? 'bg-blue-100 text-blue-700 dark:bg-blue-500/20 dark:text-blue-300 shadow-md'
              : 'text-gray-600 hover:text-blue-600 hover:bg-blue-50 dark:text-gray-300 dark:hover:text-blue-300 dark:hover:bg-white/10'
          }`}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Icon className="w-5 h-5" />
          <span>{item.label}</span>
        </motion.a>
      );
    })}
  </nav>
</div>

