// src/context/ReservationsContext.js

import { createContext, useContext, useEffect, useMemo, useState } from "react";
import { msalInstance, loginRequest } from '../msalConfig';

// Contexto
const ReservationsCtx = createContext(null);

// Función auxiliar para reconstruir fechas (strings -> Date objects)
const reviveDates = (arr) => arr.map(r => ({
    ...r,
    startTime: new Date(r.startTime),
    endTime: new Date(r.endTime),
}));

// Función auxiliar para extraer nombre de sala
const extractRoomName = (evt) => {
    // 1. Prioridad: Recurso en asistentes
    const roomAttendee = evt.attendees?.find(a => a.type === 'resource');
    if (roomAttendee) return roomAttendee.emailAddress?.name;

    // 2. Prioridad: Ubicación
    if (evt.location?.displayName && evt.location.displayName !== "Sin sala") {
        return evt.location.displayName;
    }
    return 'Sala no especificada';
};

const ROOMS = [
  { id: '1', nombre: 'Círculo de Sabios I', email: 'circulo.sabios1@fycotelecom.com' },
  { id: '2', nombre: 'Círculo de Sabios II', email: 'circulo.sabios2@fycotelecom.com' },
  { id: '3', nombre: 'Círculo de Sabios III', email: 'circulo.sabios3@fycotelecom.com' },
  { id: '4', nombre: 'Consejo de la Tribu', email: 'consejo.tribu@fycotelecom.com' },
  { id: '5', nombre: 'Plaza de Conexiones', email: 'plaza.conexiones@fycotelecom.com' },
];


export const ReservationsProvider = ({ children }) => {
    const [reservations, setReservations] = useState([]);

    // =========================================================================
    // 1. CARGA INICIAL DE DATOS (GET) - ¡ESTO ES LO QUE FALTABA!
    // =========================================================================
    useEffect(() => {
        async function fetchReservations() {
            const account = msalInstance.getActiveAccount();
            if (!account) return; // Si no hay sesión, no cargamos nada

            try {
                // Obtener token
                const tokenResponse = await msalInstance.acquireTokenSilent({
                    ...loginRequest,
                    account: account
                });
                const token = tokenResponse.accessToken;

                // Llamada a Microsoft Graph
                const response = await fetch('https://graph.microsoft.com/v1.0/me/events?$select=subject,start,end,location,id,attendees&$top=50', {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Prefer': 'outlook.timezone="America/Bogota"'
                    }
                });

                if (!response.ok) throw new Error(`Error Graph: ${response.status}`);

                const graphData = await response.json();

                // Mapear datos al formato de tu app
                const mapped = (graphData.value || []).map(evt => ({
                    id: evt.id,
                    title: evt.subject,
                    startTime: evt.start.dateTime,
                    endTime: evt.end.dateTime,
                    room: { nombre: extractRoomName(evt) },
                    attendees: evt.attendees || [],
                    location: evt.location?.displayName || ''
                }));

                // Guardar en el estado global
                setReservations(reviveDates(mapped));

            } catch (error) {
                console.error("Error cargando reservas:", error);
                // Opcional: Si el error es de auth, redirigir al login
                // msalInstance.loginRedirect(loginRequest);
            }
        }

        fetchReservations();
    }, []); // Se ejecuta una sola vez al montar el provider

    // =========================================================================
    // 2. CREAR O MODIFICAR (POST / PATCH)
    // =========================================================================
    const addOrUpdate = async (reservationPayload) => {
        const isModification = !!reservationPayload.id;
        const reservationId = reservationPayload.id;

        // Auth
        const account = msalInstance.getActiveAccount();
        if (!account) throw new Error("Sin sesión activa");

        let tokenResponse; 
        try {
            tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
        } catch {
            msalInstance.loginRedirect(loginRequest);
            return;
        }
        const token = tokenResponse.accessToken;

        // Configurar URL y Método
        const url = isModification 
            ? `https://graph.microsoft.com/v1.0/me/events/${reservationId}` 
            : 'https://graph.microsoft.com/v1.0/me/events';
        const method = isModification ? 'PATCH' : 'POST';

        // Payload para Graph
        const event = {
            subject: reservationPayload.title,
            start: { dateTime: reservationPayload.startTime, timeZone: "America/Bogota" },
            end: { dateTime: reservationPayload.endTime, timeZone: "America/Bogota" },
            location: { displayName: reservationPayload.room.nombre }, // Útil para visualización
            attendees: [{
                emailAddress: { address: reservationPayload.roomEmail, name: reservationPayload.room.nombre },
                type: "resource"
            }]
        };

        const response = await fetch(url, {
            method: method,
            headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
            body: JSON.stringify(event)
        });

        if (!response.ok) {
            const err = await response.json();
            throw new Error(err.error?.message || "Error en la API");
        }

        const apiResponse = await response.json();

        // Actualizar estado local optimistamente
        setReservations(prev => {
            const newRes = {
                ...reservationPayload,
                id: apiResponse.id,
                startTime: new Date(apiResponse.start.dateTime),
                endTime: new Date(apiResponse.end.dateTime)
            };

            if (isModification) {
                return prev.map(r => r.id === reservationId ? newRes : r);
            } else {
                return [newRes, ...prev];
            }
        });
    };

    // =========================================================================
    // 3. ELIMINAR (DELETE)
    // =========================================================================
    const removeById = async (reservationId) => {
        const account = msalInstance.getActiveAccount();
        if (!account) return;

        const tokenResponse = await msalInstance.acquireTokenSilent({ ...loginRequest, account });
        const token = tokenResponse.accessToken;

        const response = await fetch(`https://graph.microsoft.com/v1.0/me/events/${reservationId}`, {
            method: 'DELETE',
            headers: { 'Authorization': `Bearer ${token}` }
        });

        if (response.ok) {
            setReservations(prev => prev.filter(r => r.id !== reservationId));
        }
    };

    const value = useMemo(() => ({ reservations, addOrUpdate, removeById }), [reservations]);

    return <ReservationsCtx.Provider value={value}>{children}</ReservationsCtx.Provider>;
};

export const useReservations = () => {
    return useContext(ReservationsCtx);
};
