import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';
import { useReservations } from '../context/ReservationsContext';

const MyReservations = ({ userName, sidebarOpen, toggleSidebar, currentPath }) => {
  const { reservations, addOrUpdate, removeById } = useReservations();
  const [showForm, setShowForm] = useState(false);

  const mine = reservations.filter(r => r.user === userName);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />
      <motion.div className="container mx-auto px-4 py-8 max-w-6xl" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Mis Reservas</h2>
          <motion.button onClick={toggleSidebar} className="p-2 rounded-lg hover:bg-gray-100" whileTap={{ scale: 0.95 }}>
            <Menu className="w-6 h-6" />
          </motion.button>
        </div>

        <ReservationList
          reservations={mine}
          onEdit={(r) => { /* podrías abrir form con r si lo deseas */ setShowForm(true); }}
          onDelete={(id) => removeById(id)}
          isAdmin={false}
          userName={userName}
        />

        {showForm && (
          <div className="mt-8">
            <ReservationForm
              userName={userName}
              onAddReservation={(res) => { addOrUpdate(res); setShowForm(false); }}
              allReservations={reservations}
            />
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default MyReservations;
