import { useEffect, useState } from 'react';

const getInitial = () => {
  const saved = localStorage.getItem('theme');
  if (saved) return saved === 'dark';
  return window.matchMedia?.('(prefers-color-scheme: dark)').matches ?? false;
};

export function useDarkMode() {
  const [isDark, setIsDark] = useState(getInitial);

  // aplica la clase en <html>
  useEffect(() => {
    const html = document.documentElement;
    html.classList.toggle('dark', isDark);
    localStorage.setItem('theme', isDark ? 'dark' : 'light');
  }, [isDark]);

  // si cambian el sistema/otra pestaña, opcional:
  useEffect(() => {
    const media = window.matchMedia('(prefers-color-scheme: dark)');
    const onSys = (e) => !localStorage.getItem('theme') && setIsDark(e.matches);
    media.addEventListener?.('change', onSys);
    return () => media.removeEventListener?.('change', onSys);
  }, []);

  return { isDark, toggle: () => setIsDark((v) => !v) };
}
