// src/pages/CreateReservation.js

import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { useReservations } from '../context/ReservationsContext';
import ReservationForm from '../components/ReservationForm';
import { msalInstance, loginRequest } from '../msalConfig';
import Sidebar from '../components/Sidebar'; // Asegúrate de tener este import si usas Sidebar aquí
import { motion } from 'framer-motion';

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  currentPath,
}) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { reservations, addOrUpdate, rooms } = useReservations();

  // 1. ESTADO Y VERIFICACIÓN DE SESIÓN
  const [reservationToEdit, setReservationToEdit] = useState(undefined);
  const [isAuthenticated, setIsAuthenticated] = useState(!!msalInstance.getActiveAccount());
  const [isLoading, setIsLoading] = useState(true); // Nuevo estado de carga

  // Función para manejar el Login Manual
  const handleLogin = async () => {
    try {
      await msalInstance.loginPopup(loginRequest);
      const account = msalInstance.getActiveAccount();
      if (account) {
        setIsAuthenticated(true);
        window.location.reload();
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
    }
  };

  // 2. LÓGICA DE PRE-LLENADO DESDE EL CALENDARIO (Usa parámetros de URL)
  const queryParams = new URLSearchParams(location.search);
  const prefillRoomId = queryParams.get('roomId');
  const prefillDate = queryParams.get('date');
  const prefillStart = queryParams.get('start');

  let prefillData = null;

  if (prefillRoomId && prefillDate && prefillStart) {
    // Calculamos la hora de fin por defecto (1 hora después)
    const startTime = new Date(`${prefillDate}T${prefillStart}:00`);
    const endTime = new Date(startTime.getTime() + 60 * 60 * 1000); // + 60 minutos

    prefillData = {
        roomId: prefillRoomId,
        date: prefillDate,
        start: prefillStart,
        endTime: endTime.toISOString(),
    };
  }

  // 3. LÓGICA DE BÚSQUEDA REACTIVA (Para Edición)
  useEffect(() => {
    const isEditing = !!id;
    if (isEditing) {
      if (reservations.length > 0) {
        const foundReservation = reservations.find(r => r.id == id);
        setReservationToEdit(foundReservation);
        setIsLoading(false);
      }
    } else {
      // Si no estamos editando, y ya tenemos las salas (para el prefill), terminamos la carga
      if (rooms.length > 0) {
         setIsLoading(false);
      }
    }
  }, [reservations, id, rooms]);


  // --- RENDERING CONDICIONAL ---

  if (!isAuthenticated) {
    return (
      <div className="flex flex-col items-center justify-center h-[60vh] text-center p-6">
        <p className="text-red-500 mb-4">No hay sesión activa. Por favor, inicia sesión para continuar.</p>
        <button
          onClick={handleLogin}
          className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transition-all transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          Iniciar Sesión con Microsoft
        </button>
      </div>
    );
  }

  const isEditing = !!id;
  if (isEditing && isLoading) {
    // Si estamos editando y cargando, mostramos el spinner
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center">
        <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mb-2"></div>
        <p className="text-gray-600">Cargando datos de la reserva...</p>
      </div>
    );
  }

  // 4. ✅ RENDERIZADO PRINCIPAL (Usamos el layout y pasamos los datos)
  return (
    <div className="bg-transparent dark:bg-transparent text-inherit min-h-screen">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />
      <motion.div 
         className="container mx-auto px-4 py-8 max-w-2xl" 
         initial={{ opacity: 0 }} 
         animate={{ opacity: 1 }}
      >
        <ReservationForm
          // Usamos el ID como key para forzar reinicio si cambia la URL
          key={id || 'new-reservation'} 
          userName={userName}
          reservationToEdit={reservationToEdit}
          prefillData={prefillData} // PASAMOS LOS DATOS PRE-LLENADOS DEL CALENDARIO
          onUpsert={addOrUpdate}
          onCancel={() => navigate('/my-reservations')}
        />
      </motion.div>
    </div>
  );
};

export default CreateReservation;
