// src/components/ThemeToggle.jsx
import React, { useEffect, useState } from 'react';
import { Sun, Moon, Laptop } from 'lucide-react';
import { getSavedTheme, setTheme } from '../utils/theme';
import { motion, AnimatePresence, useReducedMotion } from 'framer-motion';

const order = ['light', 'dark', 'system'];
const icons = { light: Sun, dark: Moon, system: Laptop };
const labels = { light: 'Claro', dark: 'Oscuro', system: 'Sistema' };

const ThemeToggle = () => {
  const [pref, setPref] = useState('system');
  const shouldReduce = useReducedMotion();

  useEffect(() => setPref(getSavedTheme()), []);

  const cycle = () => {
    const next = order[(order.indexOf(pref) + 1) % order.length];
    setPref(next);
    setTheme(next);
  };

  const title = `Tema: ${labels[pref]}`;

  // Variantes de animación (respetan reduce-motion)
  const variants = {
    initial: { opacity: 0, rotate: shouldReduce ? 0 : -90, scale: shouldReduce ? 1 : 0.8, y: shouldReduce ? 0 : -4 },
    animate: { opacity: 1, rotate: 0, scale: 1, y: 0, transition: { type: 'spring', stiffness: 420, damping: 26 } },
    exit:    { opacity: 0, rotate: shouldReduce ? 0 : 90, scale: shouldReduce ? 1 : 0.8, y: shouldReduce ? 0 : 4, transition: { duration: 0.18 } },
  };

  const Icon = icons[pref] ?? Laptop;

  return (
    <motion.button
      onClick={cycle}
      title={title}
      aria-label={title}
      className="h-10 w-10 inline-flex items-center justify-center rounded-full
                 border border-gray-200/80 bg-white/70 backdrop-blur
                 text-gray-700 hover:bg-gray-50
                 dark:border-gray-700/70 dark:bg-gray-900/50 dark:text-gray-200 dark:hover:bg-white/10
                 transition-colors shadow-sm"
      whileTap={{ scale: 0.94 }}
    >
      <div className="relative h-5 w-5">
        <AnimatePresence mode="popLayout" initial={false}>
          <motion.span
            key={pref}                 // ← cambia al cambiar el tema
            className="absolute inset-0 flex items-center justify-center"
            variants={variants}
            initial="initial"
            animate="animate"
            exit="exit"
          >
            <Icon className="w-5 h-5" />
          </motion.span>
        </AnimatePresence>
      </div>
      <span className="sr-only">{title}</span>
    </motion.button>
  );
};

export default ThemeToggle;
