// src/pages/AdminPanel.js
import React, { useEffect, useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import { Menu, Search, RefreshCw, FileDown } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';
import { useReservations } from '../context/ReservationsContext';
import { deleteReservationInOutlookIfAny } from '../utils/deleteReservation';
import { initialReservations } from '../utils/reservations';
import { downloadReservationsCSV } from '../utils/csv';

// ---------- helpers ----------
const useDebounced = (value, ms = 250) => {
  const [v, setV] = useState(value);
  useEffect(() => {
    const t = setTimeout(() => setV(value), ms);
    return () => clearTimeout(t);
  }, [value, ms]);
  return v;
};

const groupByDay = (list) =>
  list.reduce((acc, r) => {
    const d = new Date(r.startTime);
    const key = d.toLocaleDateString('es-ES', {
      weekday: 'short',
      day: '2-digit',
      month: 'short',
      year: 'numeric',
    });
    (acc[key] ||= []).push(r);
    return acc;
  }, {});

// --------------------------------

const AdminPanel = ({ sidebarOpen, toggleSidebar, currentPath }) => {
  const { reservations, addOrUpdate, removeById } = useReservations();

  const [searchTerm, setSearchTerm] = useState('');
  const [filterRoom, setFilterRoom] = useState('all');
  const [filterDate, setFilterDate] = useState(''); // yyyy-MM-dd
  const [editing, setEditing] = useState(null);
  const [resetInfo, setResetInfo] = useState('');

  const debounced = useDebounced(searchTerm, 250);

  const filtered = useMemo(() => {
    const q = debounced.trim().toLowerCase();
    return reservations.filter((r) => {
      const roomName = r.room?.nombre || '';
      const textOk =
        r.title.toLowerCase().includes(q) ||
        r.user.toLowerCase().includes(q) ||
        roomName.toLowerCase().includes(q);

      const roomOk = filterRoom === 'all' ? true : r.room?.id === filterRoom;

      const dateOk = filterDate
        ? new Date(r.startTime).toISOString().slice(0, 10) === filterDate
        : true;

      return textOk && roomOk && dateOk;
    });
  }, [reservations, debounced, filterRoom, filterDate]);

  const handleDelete = async (id) => {
    const target = reservations.find((r) => r.id === id);
    if (target) {
      await deleteReservationInOutlookIfAny(target);
    }
    removeById(id);
  };

  const handleResetLocalSeed = async () => {
    try {
      // solo resetea localStorage; no borra/crea en Outlook
      localStorage.removeItem('reservations');
      const seeded = initialReservations.map((r) => ({
        ...r,
        startTime: new Date(r.startTime).toISOString(),
        endTime: new Date(r.endTime).toISOString(),
      }));
      localStorage.setItem('reservations', JSON.stringify(seeded));
      setResetInfo('Reservas reseteadas localmente. Recarga la página para ver los cambios.');
    } catch {
      setResetInfo('No se pudo resetear. Revisa permisos del navegador.');
    }
  };

  const resetFilters = () => {
    setSearchTerm('');
    setFilterRoom('all');
    setFilterDate('');
  };

  const grouped = useMemo(() => groupByDay(filtered), [filtered]);

        // Atajo de teclado para enfocar búsqueda (⌘K / Ctrl+K)
      useEffect(() => {
        const handleKeyDown = (e) => {
          if ((e.metaKey || e.ctrlKey) && e.key.toLowerCase() === 'k') {
            e.preventDefault();
            const input = document.getElementById('search-admin');
            input?.focus();
          }
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
      }, []);


  return (
    //<div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-gray-950 dark:via-slate-950 dark:to-black transition-colors">
    <div className="bg-transparent dark:bg-transparent text-inherit">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />

      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        {/* encabezado móvil */}
        <div className="text-gray-900 dark:text-gray-100 font-semibold text-[15px]">
          <h2 className="text-xl font-semibold">Panel Admin</h2>
          <motion.button
            onClick={toggleSidebar}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-white/10"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6" />
          </motion.button>
        </div>

        {/* toolbar */}
        <div className="grid grid-cols-1 md:grid-cols-[1fr,200px,180px,auto,auto,auto] gap-3 mb-6 items-center">
          {/* búsqueda */}
          <div className="relative">
            <Search className="w-5 h-5 absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
            <input
              id="search-admin"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar por título, usuario o sala..."
              className="w-full pl-10 pr-3 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                         bg-white dark:bg-gray-900/50 text-gray-800 dark:text-gray-100 
                         outline-none focus:ring-2 focus:ring-blue-500 transition-colors"
            />
          </div>

          {/* sala */}
          <select
            value={filterRoom}
            onChange={(e) => setFilterRoom(e.target.value)}
            className="px-3 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                       bg-white dark:bg-gray-900/50 text-gray-800 dark:text-gray-100"
            title="Filtrar por sala"
          >
            <option value="all">Todas las salas</option>
            <option value="1">Círculo de Sabios I</option>
            <option value="2">Círculo de Sabios II</option>
            <option value="3">Círculo de Sabios III</option>
            <option value="4">Consejo de la Tribu</option>
            <option value="5">Plaza de Conexiones</option>
          </select>

          {/* fecha */}
          <input
            type="date"
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value)}
            className="px-3 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                       bg-white dark:bg-gray-900/50 text-gray-800 dark:text-gray-100"
            title="Filtrar por fecha"
          />

          {/* export */}
          <motion.button
            type="button"
            onClick={() => downloadReservationsCSV(filtered, 'reservas-filtradas.csv')}
            className="inline-flex items-center gap-2 px-3 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                       text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/10 transition-colors disabled:opacity-50"
            disabled={!filtered.length}
            whileTap={{ scale: 0.98 }}
            title="Exportar CSV"
          >
            <FileDown className="w-4 h-4" />
            Exportar CSV
          </motion.button>

          {/* reset filtros */}
          <motion.button
            type="button"
            onClick={resetFilters}
            className="inline-flex items-center justify-center h-10 w-10 rounded-xl border border-gray-200 dark:border-gray-700 
                       text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/10 transition-colors"
            whileTap={{ scale: 0.95 }}
            title="Reset filtros"
          >
            ⟲
          </motion.button>

          {/* reset local (opcional pruebas) */}
          <motion.button
            type="button"
            onClick={handleResetLocalSeed}
            className="hidden md:inline-flex items-center gap-2 px-3 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                       text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/10"
            title="Resetear reservas locales (solo pruebas)"
            whileTap={{ scale: 0.98 }}
          >
            <RefreshCw className="w-4 h-4" />
            Reset local
          </motion.button>
        </div>

        {resetInfo && (
          <div className="mb-4 text-sm text-blue-700 bg-blue-50 border border-blue-200 rounded-xl p-3
                          dark:text-blue-300 dark:bg-blue-500/10 dark:border-blue-500/20">
            {resetInfo}
          </div>
        )}

        {/* listado agrupado por día */}
        {Object.keys(grouped).length === 0 ? (
          <div className="rounded-3xl p-8 md:p-10 bg-white/80 dark:bg-white/5 border border-gray-200/50 dark:border-gray-800/50
                          backdrop-blur-xl shadow-xl text-center transition-colors">
            <h3 className="text-base md:text-lg font-semibold text-gray-800 dark:text-gray-100">
              No hay resultados con los filtros actuales
            </h3>
            <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
              Ajusta la búsqueda, cambia la sala o limpia los filtros.
            </p>
            <button
              onClick={resetFilters}
              className="mt-4 inline-flex items-center gap-2 px-4 py-2 rounded-xl border border-gray-200 dark:border-gray-700 
                         text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-white/10 transition-colors"
            >
              Reset filtros
            </button>
          </div>
        ) : (
          Object.entries(grouped).map(([day, items]) => (
            <div key={day} className="mb-6">
              <div className="sticky top-16 z-10 -mx-4 px-4">
                <div className="flex items-center gap-3">
                  <div className="h-px flex-1 bg-gray-200/60 dark:bg-gray-700/60" />
                  <span className="text-xs font-medium uppercase tracking-wide text-gray-500 dark:text-gray-400">
                    {day}
                  </span>
                  <div className="h-px flex-1 bg-gray-200/60 dark:bg-gray-700/60" />
                </div>
              </div>

              <ReservationList
                reservations={items}
                onEdit={(r) => setEditing(r)}
                onDelete={handleDelete}
                onResync={async (updated) => addOrUpdate(updated)}
                isAdmin
              />
            </div>
          ))
        )}

        <div className="mt-6 text-center text-xs text-gray-500 dark:text-gray-400">
          Total de reservas: <span className="font-medium">{reservations.length}</span> · Filtradas:{' '}
          <span className="font-medium">{filtered.length}</span>
        </div>

        {editing && (
          <div className="mt-8">
            <ReservationForm
              userName={editing.user}
              reservationToEdit={editing}
              allReservations={reservations}
              onUpsert={(res) => {
                addOrUpdate(res);
                setEditing(null);
              }}
            />
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default AdminPanel;
