import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Plus, Calendar, Clock, User, Edit, Trash } from 'lucide-react';
import { format, addHours } from 'date-fns';

const ReservationForm = ({ onAddReservation, userName = 'Tú' }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    room: 'Sala A',
    startTime: new Date(),
    duration: 1
  });
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState(null);

  const rooms = ['Sala A', 'Sala B', 'Sala C', 'Sala de Conferencias'];
  const durations = [1, 2, 3, 4, 5];

  const handleSubmit = (e) => {
    e.preventDefault();
    const endTime = addHours(new Date(formData.startTime), formData.duration);
    const reservation = {
      ...formData,
      endTime,
      user: userName,
      status: 'confirmed',
      id: editingId || Date.now().toString()
    };
    if (editingId) {
      onAddReservation(reservation, 'edit');
      setEditingId(null);
    } else {
      onAddReservation(reservation);
    }
    setFormData({ title: '', description: '', room: 'Sala A', startTime: new Date(), duration: 1 });
  };

  const handleEdit = (reservation) => {
    setEditingId(reservation.id);
    setEditData(reservation);
    setFormData({
      title: reservation.title,
      description: reservation.description,
      room: reservation.room,
      startTime: reservation.startTime,
      duration: Math.abs((reservation.endTime - reservation.startTime) / (1000 * 60 * 60))
    });
  };

  const startTimeFormatted = format(formData.startTime, 'PPPp');

  return (
    <motion.div
      className="bg-white/90 backdrop-blur-xl border border-gray-200/50 rounded-3xl p-8 shadow-xl"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-2">
        {editingId ? <Edit className="w-6 h-6 text-blue-600" /> : <Plus className="w-6 h-6 text-green-600" />}
        {editingId ? 'Editar Reserva' : 'Nueva Reserva'}
      </h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Título</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Ej: Reunión equipo"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Sala</label>
            <select
              value={formData.room}
              onChange={(e) => setFormData({ ...formData, room: e.target.value })}
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              {rooms.map((room) => (
                <option key={room} value={room}>{room}</option>
              ))}
            </select>
          </div>
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Descripción</label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            rows={3}
            className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Detalles de la reserva..."
          />
        </div>
        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Fecha y Hora Inicio</label>
            <input
              type="datetime-local"
              value={format(formData.startTime, 'yyyy-MM-ddTHH:mm')}
              onChange={(e) => setFormData({ ...formData, startTime: new Date(e.target.value) })}
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <p className="text-sm text-gray-500 mt-1">{startTimeFormatted}</p>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Duración (horas)</label>
            <select
              value={formData.duration}
              onChange={(e) => setFormData({ ...formData, duration: parseInt(e.target.value) })}
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              {durations.map((d) => (
                <option key={d} value={d}>{d} hora{d > 1 ? 's' : ''}</option>
              ))}
            </select>
          </div>
        </div>
        <motion.button
          type="submit"
          className="w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white py-3 px-6 rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all duration-300"
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
        >
          {editingId ? 'Actualizar Reserva' : 'Crear Reserva'}
        </motion.button>
      </form>

      {editData && (
        <motion.div
          className="mt-8 p-6 bg-yellow-50 border border-yellow-200 rounded-xl"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <h3 className="font-semibold text-yellow-800 mb-2">Editando:</h3>
          <p className="text-sm text-yellow-700">{editData.title} - {editData.room} ({format(editData.startTime, 'PPPp')} - {format(editData.endTime, 'p')})</p>
          <motion.button
            onClick={() => {
              setEditingId(null);
              setEditData(null);
              setFormData({ title: '', description: '', room: 'Sala A', startTime: new Date(), duration: 1 });
            }}
            className="mt-3 flex items-center gap-2 text-red-600 hover:text-red-800"
            whileHover={{ scale: 1.05 }}
          >
            <Trash className="w-4 h-4" /> Cancelar Edición
          </motion.button>
        </motion.div>
      )}
    </motion.div>
  );
};

export default ReservationForm;
