import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Download, AlertCircle } from 'lucide-react';
import { PublicClientApplication } from '@azure/msal-browser';

const msalConfig = {
  auth: {
    clientId: '8d23218d-b8bc-4af1-b072-c48bfbe0c1d3',
    authority: 'https://login.microsoftonline.com/0c4fa9d7-8fb7-4395-99a2-3674e4f8f773',
    redirectUri: window.location.origin,
  },
};

const msalInstance = new PublicClientApplication(msalConfig);

const rooms = [
  { id: '1', nombre: "Círculo de Sabios I", capacidad: 5 },
  { id: '2', nombre: "Círculo de Sabios II", capacidad: 5 },
  { id: '3', nombre: "Círculo de Sabios III", capacidad: 5 },
  { id: '4', nombre: "Consejo de la Tribu", capacidad: 12 },
  { id: '5', nombre: "Plaza de Conexiones", capacidad: 6 }
];

const OutlookSync = () => {
  const [msalReady, setMsalReady] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [accessToken, setAccessToken] = useState(null);
  const [selectedRoom, setSelectedRoom] = useState(rooms[0]);

  useEffect(() => {
    msalInstance.initialize().then(() => {
      setMsalReady(true);
      const accounts = msalInstance.getAllAccounts();
      if (accounts.length > 0) {
        setIsAuthenticated(true);
        const request = {
          scopes: ['Calendars.ReadWrite'],
          account: accounts[0],
        };
        msalInstance.acquireTokenSilent(request).then(response => {
          setAccessToken(response.accessToken);
        }).catch(() => {
          msalInstance.acquireTokenPopup(request).then(response => {
            setAccessToken(response.accessToken);
          });
        });
      }
    }).catch(() => {
      setMsalReady(true);
    });
  }, []);

  const handleLogin = () => {
    if (!msalReady) return;
    msalInstance.loginPopup({ scopes: ['Calendars.ReadWrite'] }).then(loginResponse => {
      setIsAuthenticated(true);
      const request = {
        scopes: ['Calendars.ReadWrite'],
        account: loginResponse.account,
      };
      msalInstance.acquireTokenSilent(request).then(tokenResponse => {
        setAccessToken(tokenResponse.accessToken);
      }).catch(() => {
        msalInstance.acquireTokenPopup(request).then(tokenResponse => {
          setAccessToken(tokenResponse.accessToken);
        });
      });
    });
  };

  const createTestEvent = () => {
    if (!msalReady || !accessToken) return;
    const event = {
      subject: "Evento de prueba",
      body: {
        contentType: "HTML",
        content: "Este es un evento de prueba creado desde la app."
      },
      start: {
        dateTime: new Date(new Date().getTime() + 3600000).toISOString(),
        timeZone: "UTC"
      },
      end: {
        dateTime: new Date(new Date().getTime() + 7200000).toISOString(),
        timeZone: "UTC"
      },
      location: {
        displayName: `${selectedRoom.nombre} (Capacidad: ${selectedRoom.capacidad})`
      }
    };

    fetch('https://graph.microsoft.com/v1.0/me/events', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(event)
    }).then(response => {
      if (response.ok) {
        alert('Evento creado exitosamente en Outlook.');
      } else {
        alert('Error al crear el evento.');
      }
    }).catch(() => {
      alert('Error al crear el evento.');
    });
  };

  return (
    <motion.div
      className="bg-white/80 backdrop-blur-xl border border-gray-200/50 rounded-3xl p-6 shadow-xl text-center"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: 0.2 }}
    >
      {!isAuthenticated ? (
        <AlertCircle className="w-12 h-12 text-yellow-500 mx-auto mb-4" />
      ) : (
        <Download className="w-12 h-12 text-green-500 mx-auto mb-4" />
      )}
      <h3 className="text-lg font-bold text-gray-800 mb-2">Sincronización con Outlook</h3>
      <p className="text-gray-600 mb-4">
        ¡Pronto disponible! Una vez conectada la base de datos, podrás sincronizar tus reservas directamente con tu calendario de Outlook.
      </p>
      {!isAuthenticated ? (
        <motion.button
          className="bg-gradient-to-r from-blue-500 to-purple-600 text-white px-6 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-2 mx-auto"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLogin}
        >
          Iniciar sesión con Microsoft
        </motion.button>
      ) : (
        <>
          <select
            className="mb-4 px-4 py-2 rounded-lg border border-gray-300 text-gray-700 w-full max-w-xs mx-auto block"
            value={selectedRoom.id}
            onChange={(e) => {
              const room = rooms.find(r => r.id === e.target.value);
              setSelectedRoom(room);
            }}
          >
            {rooms.map(room => (
              <option key={room.id} value={room.id}>
                {room.nombre} (Capacidad: {room.capacidad})
              </option>
            ))}
          </select>
          <motion.button
            className="bg-gradient-to-r from-green-500 to-green-700 text-white px-6 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-2 mx-auto"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={createTestEvent}
          >
            Crear evento de prueba en Outlook
          </motion.button>
        </>
      )}
    </motion.div>
  );
};

export default OutlookSync;
