import { PublicClientApplication } from "@azure/msal-browser";

const msalConfig = {
  auth: {
    clientId: "8d23218d-b8bc-4af1-b072-c48bfbe0c1d3",
    //authority: "https://login.microsoftonline.com/0c4fa9d7-8fb7-4395-99a2-3674e4f8f773",//
    authority: "https://login.microsoftonline.com/common",
    redirectUri: window.location.origin,
    postLogoutRedirectUri: window.location.origin,
  },
  cache: {
    cacheLocation: "localStorage", // evita perder sesi칩n en recargas
    storeAuthStateInCookie: false,
  },
system: {
      loggerOptions: {
          loggerCallback: (level, message, containsPii) => {
              if (containsPii) { return; }
              console.error("MSAL DEBUG:", message); // <--- Esto nos dir치 la verdad
          },
          logLevel: "Error"
      }

}

};

export const loginRequest = {
  scopes: ["User.Read", "Calendars.ReadWrite"],
};

export const msalInstance = new PublicClientApplication(msalConfig);
