import { createContext, useContext, useEffect, useMemo, useState } from "react";
import { initialReservations } from "../utils/reservations";

// Helpers para fechas
const reviveDates = (arr) =>
  arr.map(r => ({
    ...r,
    startTime: new Date(r.startTime),
    endTime: new Date(r.endTime),
  }));

const toISO = (r) => ({
  ...r,
  startTime: new Date(r.startTime).toISOString(),
  endTime: new Date(r.endTime).toISOString(),
});

const ReservationsCtx = createContext(null);

export const ReservationsProvider = ({ children }) => {
  const [reservations, setReservations] = useState([]);

  // Cargar del localStorage o sembrar initialReservations
  useEffect(() => {
    try {
      const raw = localStorage.getItem("reservations");
      if (raw) {
        const parsed = JSON.parse(raw);
        setReservations(reviveDates(parsed));
      } else {
        const seeded = initialReservations.map(toISO);
        localStorage.setItem("reservations", JSON.stringify(seeded));
        setReservations(reviveDates(seeded));
      }
    } catch {
      const seeded = initialReservations.map(toISO);
      setReservations(reviveDates(seeded));
    }
  }, []);

  // Guardar cada vez que cambien
  useEffect(() => {
    if (!reservations || reservations.length === 0) return;
    try {
      const serializable = reservations.map(toISO);
      localStorage.setItem("reservations", JSON.stringify(serializable));
    } catch {}
  }, [reservations]);

  // CRUD básico
  const addOrUpdate = (res) => {
    setReservations(prev => {
      const i = prev.findIndex(x => x.id === res.id);
      if (i >= 0) {
        const copy = [...prev];
        copy[i] = res;
        return copy;
      }
      return [res, ...prev];
    });
  };

  const removeById = (id) => setReservations(prev => prev.filter(r => r.id !== id));

  const value = useMemo(() => ({ reservations, addOrUpdate, removeById }), [reservations]);

  return <ReservationsCtx.Provider value={value}>{children}</ReservationsCtx.Provider>;
};

export const useReservations = () => {
  const ctx = useContext(ReservationsCtx);
  if (!ctx) throw new Error("useReservations debe usarse dentro de <ReservationsProvider>");
  return ctx;
};
