import { useEffect, useState } from 'react';
// IMPORTAMOS MSAL PARA PEDIR EL TOKEN NOSOTROS MISMOS
import { msalInstance, loginRequest } from '../msalConfig';

export default function MyReservations() {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    let alive = true;

    async function fetchReservations() {
      setLoading(true);
      setError('');

      try {
        // 1. VERIFICAR SI HAY USUARIO
        const account = msalInstance.getActiveAccount();
        if (!account) {
          throw new Error('No hay sesión activa. Por favor inicia sesión arriba.');
        }

        // 2. OBTENER TOKEN SILENCIOSAMENTE
        // (Pedimos la llave sin molestar al usuario)
        const tokenResponse = await msalInstance.acquireTokenSilent({
            ...loginRequest,
            account: account
        });

        const token = tokenResponse.accessToken;

        // 3. LLAMAR A MICROSOFT GRAPH
        const response = await fetch('https://graph.microsoft.com/v1.0/me/events?$select=subject,start,end,location,id&$top=50', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
            'Prefer': 'outlook.timezone="America/Bogota"'
          }
        });

        if (!response.ok) {
            throw new Error(`Error Microsoft: ${response.status}`);
        }

        const graphData = await response.json();

        // 4. ADAPTAR DATOS PARA TU LISTA
        const mapped = (graphData.value || []).map(evt => ({
            id: evt.id,
            title: evt.subject,
            startTime: evt.start.dateTime,
            endTime: evt.end.dateTime,
            room: { nombre: evt.location.displayName || 'Sala no especificada' }
        }));

        if (alive) setReservations(mapped);

      } catch (e) {
        console.error(e);
        // Si el error es que necesita login interactivo, no mostramos error rojo, solo avisamos
        if (alive) setError(e.message || 'No se pudieron cargar las reservas');
      } finally {
        if (alive) setLoading(false);
      }
    }

    fetchReservations();

    return () => { alive = false; };
  }, []); // Ya no dependemos de props externas


  // Dentro de MyReservations.js, antes de la función hhmm
const getRoomName = (reservation) => {
    // PRIORIDAD 1: Buscar el objeto 'room' que creamos localmente (para reservas nuevas)
    if (reservation.room?.nombre) {
        return reservation.room.nombre;
    }

    // PRIORIDAD 2: Buscar el recurso dentro del array de asistentes (para reservas antiguas de Outlook)
    const roomAttendee = reservation.attendees?.find(a => a.type === 'resource');

    if (roomAttendee) {
        // Devolvemos el nombre del recurso o el email
        return roomAttendee.emailAddress?.name || roomAttendee.emailAddress?.address;
    }

    // PRIORIDAD 3: Usar el display name como fallback (menos fiable)
    if (reservation.location?.displayName && reservation.location.displayName !== "Sin sala") {
        return reservation.location.displayName;
    }

    return 'Sala no especificada';
};


  // --- RENDERIZADO (IGUAL QUE ANTES) ---
  const hhmm = (d) => d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  return (
    <>
      {error && (
        <div className="mx-auto max-w-4xl mt-6 rounded-xl border border-red-200 bg-red-50 px-4 py-3 text-sm text-red-800">
          {error}
        </div>
      )}

      {loading && !error && (
        <div className="mx-auto max-w-4xl mt-6 text-sm text-gray-600">
          Cargando tus reservas de Outlook...
        </div>
      )}

      {!loading && !error && reservations.length === 0 && (
        <div className="mx-auto max-w-4xl mt-10 p-6 text-center border rounded-2xl">
          <p className="text-gray-700">No tienes reservas próximas en Outlook.</p>
        </div>
      )}

      {!loading && !error && reservations.length > 0 && (
        <ul className="mx-auto max-w-4xl mt-4 space-y-3">
          {reservations.map((r) => {
            const start = new Date(r.startTime);
            const end = new Date(r.endTime);
            const dateStr = start.toLocaleDateString([], { day: '2-digit', month: 'short' });

            return (
              <li key={r.id} className="rounded-xl bg-white border border-gray-200 px-4 py-3">
                <div className="font-semibold">{r.title || '(Sin título)'}</div>
                <div className="text-sm text-gray-600">
                  {getRoomName(r)} · {dateStr}, {hhmm(start)}–{hhmm(end)}
                </div>
              </li>
            );
          })}
        </ul>
      )}
    </>
  );
}
