import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';
import { useReservations } from '../context/ReservationsContext';
import { deleteReservationInOutlookIfAny } from '../utils/deleteReservation'; // üëà NUEVO

const MyReservations = ({ userName, sidebarOpen, toggleSidebar, currentPath }) => {
  const { reservations, addOrUpdate, removeById } = useReservations();
  const [showForm, setShowForm] = useState(false);
  const [editing, setEditing] = useState(null); // reserva seleccionada para editar

  const mine = reservations.filter(r => r.user === userName);

  const handleCreateNew = () => {
    setEditing(null);
    setShowForm(true);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />

      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Mis Reservas</h2>
          <motion.button
            onClick={toggleSidebar}
            className="p-2 rounded-lg hover:bg-gray-100"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6" />
          </motion.button>
        </div>

        <ReservationList
          reservations={mine}
          onEdit={(res) => { setEditing(res); setShowForm(true); }}
          onDelete={async (id) => {
            // üëá Elimina primero en Outlook (si aplica) y luego local
            const target = reservations.find(r => r.id === id);
            if (target) {
              await deleteReservationInOutlookIfAny(target);
            }
            removeById(id);
          }}
          isAdmin={false}
          userName={userName}
        />

        {/* Bot√≥n flotante para crear nueva (en m√≥vil) */}
        <motion.button
          onClick={handleCreateNew}
          className="fixed bottom-8 right-8 bg-gradient-to-r from-indigo-600 to-blue-600 text-white px-4 py-3 rounded-full shadow-lg md:hidden"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Nueva reserva
        </motion.button>

        {/* Formulario (crear/editar) en desktop o cuando se active en m√≥vil */}
        {(showForm || editing) && (
          <div className="mt-8">
            <ReservationForm
              userName={userName}
              reservationToEdit={editing}
              allReservations={reservations}
              onUpsert={(res) => {
                addOrUpdate(res);
                setEditing(null);
                setShowForm(false);
              }}
            />
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default MyReservations;
