import React, { useState } from 'react';
import { format } from "date-fns";
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';
import ReservationForm from '../components/ReservationForm';

const MyReservations = ({ reservations, onAddReservation, onEdit, onDelete, userName, sidebarOpen, toggleSidebar, currentPath }) => {
  const [showForm, setShowForm] = useState(false);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />
      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="md:hidden mb-4">
          <motion.button
            onClick={toggleSidebar}
            className="p-3 bg-white rounded-xl shadow-sm"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6 text-gray-700" />
          </motion.button>
        </div>
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Mis Reservas</h1>
        {/*
          Format reservation times into a user-friendly string before passing to ReservationList
        */}
        {(() => {
          const formattedReservations = reservations
            .filter(r => r.user && r.user.toLowerCase() === userName.toLowerCase())
            .map(r => ({
              ...r,
              friendlyTime: `${format(new Date(r.startTime), 'PPPp')} - ${format(new Date(r.endTime), 'p')}`
            }));
          console.log("formattedReservations:", formattedReservations);
          return (
            <ReservationList
              reservations={formattedReservations}
              onEdit={onEdit}
              onDelete={onDelete}
              userName={userName}
            />
          );
        })()}
        {showForm && (
          <ReservationForm onAddReservation={onAddReservation} userName={userName} />
        )}
        <motion.button
          onClick={() => setShowForm(!showForm)}
          className="fixed bottom-8 right-8 bg-gradient-to-r from-green-500 to-emerald-600 text-white p-4 rounded-full shadow-lg md:relative md:bottom-auto md:right-auto md:mt-8"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Menu className="w-6 h-6" />
        </motion.button>
      </motion.div>
    </div>
  );
};

export default MyReservations;
