import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Menu, Search } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationList from '../components/ReservationList';

const AdminPanel = ({ reservations, onEdit, onDelete, sidebarOpen, toggleSidebar, currentPath }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const filtered = reservations.filter(r =>
    r.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    r.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
    r.room.nombre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />
      <motion.div
        className="container mx-auto px-4 py-8 max-w-6xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="md:hidden mb-4">
          <motion.button
            onClick={toggleSidebar}
            className="p-3 bg-white rounded-xl shadow-sm"
            whileTap={{ scale: 0.95 }}
          >
            <Menu className="w-6 h-6 text-gray-700" />
          </motion.button>
        </div>
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Panel de Administraci√≥n</h1>
          <div className="relative w-full sm:w-64">
            <Search className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar reservas..."
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
        </div>
        <ReservationList
          reservations={filtered}
          onEdit={onEdit}
          onDelete={onDelete}
          isAdmin={true}
        />
        <p className="text-sm text-gray-500 mt-4 text-center">
          Total de reservas: {reservations.length} | Filtradas: {filtered.length}
        </p>
      </motion.div>
    </div>
  );
};

export default AdminPanel;
