import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import { useReservations } from '../context/ReservationsContext';
import { useParams } from 'react-router-dom'; //Para leer el ID de la URL

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  currentPath,
  onUpsert,
}) => {
  const { id } = useParams(); 
  const { reservations, addOrUpdate } = useReservations(); // Trae la lista y la función
  const navigate = useNavigate(); // Necesario si usas onCancel/redirección

  // 1. NUEVO ESTADO PARA LA RESERVA EN EDICIÓN
  //const [reservationToEdit, setReservationToEdit] = useState(undefined);
  const reservationToEdit = reservations.find(r => r.id == id);

  const isEditing = !!id;
  // 2. LÓGICA DE ESPERA (Guard Clause)
  if (isEditing && !reservationToEdit) {
    // Si estamos editando y aún no encontramos el objeto, mostramos la carga.
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center text-xl font-medium text-gray-700 dark:text-gray-300">
        Cargando datos de la reserva...
      </div>
    );
  }

  return (
    // ... tu layout ...
      <ReservationForm
        // Usamos el ID como clave. Si el ID cambia (de undefined a un valor), 
        // React destruye y recrea el formulario, forzando la correcta inicialización.
        key={id || 'new'} 
        userName={userName}
        reservationToEdit={reservationToEdit} 
        onUpsert={addOrUpdate} // Asumo que onUpsert viene de la lógica de arriba
        onCancel={() => navigate('/my-reservations')} // Lógica de ejemplo para cancelar
      />
    // ...
  );
};

export default CreateReservation;
