import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import Sidebar from '../components/Sidebar';
import { useReservations } from '../context/ReservationsContext';

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  reservationToEdit,
  onClearEdit,
  currentPath
}) => {
  const { reservations, addOrUpdate } = useReservations();

  useEffect(() => {
    // Si quisieras efectos al entrar aquí, los mantienes
  }, []);

  return (
    <div className="min-h-screen">
      <Sidebar isOpen={sidebarOpen} onClose={toggleSidebar} currentPath={currentPath} />
      <motion.div className="container mx-auto px-4 py-8 max-w-6xl" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <div className="flex items-center justify-between mb-6 md:hidden">
          <h2 className="text-xl font-semibold">Crear Reserva</h2>
          <motion.button onClick={toggleSidebar} className="p-2 rounded-lg hover:bg-gray-100" whileTap={{ scale: 0.95 }}>
            <Menu className="w-6 h-6 text-gray-700" />
          </motion.button>
        </div>

        <ReservationForm
          userName={userName}
          reservationToEdit={reservationToEdit}
          onAddReservation={(res) => { addOrUpdate(res); onClearEdit?.(); }}
          allReservations={reservations}  // útil si luego validas conflictos
        />

        <OutlookSync />
      </motion.div>
    </div>
  );
};

export default CreateReservation;
