import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import { useReservations } from '../context/ReservationsContext';
import { useParams } from 'react-router-dom'; //Para leer el ID de la URL

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  currentPath,
  onUpsert,
}) => {
  const { id } = useParams(); 
  const { reservations, addOrUpdate } = useReservations(); // Trae la lista y la funci贸n
  const navigate = useNavigate(); // Necesario si usas onCancel/redirecci贸n

  // 1. NUEVO ESTADO PARA LA RESERVA EN EDICIN
  //const [reservationToEdit, setReservationToEdit] = useState(undefined);
  const reservationToEdit = reservations.find(r => r.id == id);

  const isEditing = !!id;
  // 2. LGICA DE ESPERA (Guard Clause)
  if (isEditing && !reservationToEdit) {
    // Si estamos editando y a煤n no encontramos el objeto, mostramos la carga.
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center text-xl font-medium text-gray-700 dark:text-gray-300">
        Cargando datos de la reserva...
      </div>
    );
  }

  // 3. LGICA DE ESPERA EN EL RENDER
  if (isEditing && !reservationToEdit) {
    // Se muestra mientras el useEffect busca la reserva
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center">
        Cargando datos de la reserva...
      </div>
    );
  }

  // 4. PASAR EL OBJETO ENCONTRADO AL FORMULARIO
  return (
    // ... el resto del layout ...
      <ReservationForm
        userName={userName}
        reservationToEdit={reservationToEdit} //  Usa el objeto de estado
        onUpsert={addOrUpdate}
      />
    // ...
  );
};

export default CreateReservation;
