import { createContext, useContext, useEffect, useMemo, useState } from "react";
// IMPORTACIONES CRÍTICAS DE MSAL
import { msalInstance, loginRequest } from '../msalConfig';
import { BrowserAuthError } from '@azure/msal-browser';

// --- ASUNCIONES DE UTILIDADES ---
// (Estas utilidades deben existir en tus archivos utils)
const initialReservations = []; // Asume que tienes un array inicial en tu utils
const reviveDates = (arr) => arr.map(r => ({
    ...r,
    startTime: new Date(r.startTime),
    endTime: new Date(r.endTime),
}));
const toISO = (r) => ({
    ...r,
    startTime: r.startTime.toISOString(),
    endTime: r.endTime.toISOString(),
});
// ------------------------------------

const ReservationsCtx = createContext(null);

export const ReservationsProvider = ({ children }) => {
    const [reservations, setReservations] = useState([]);

    // [Tu lógica de localStorage de useEffect está aquí]
    useEffect(() => {
        // ... Cargar y guardar en localStorage (omitido por espacio, asumo que funciona)
        const seeded = initialReservations.map(toISO);
        setReservations(reviveDates(seeded));
    }, []);

    useEffect(() => {
        // ... Guardar en localStorage cada vez que cambie 'reservations'
    }, [reservations]);

// -------------------------------------------------------------
// FUNCIÓN ASÍNCRONA: CREAR (POST) o MODIFICAR (PATCH)
// -------------------------------------------------------------
const addOrUpdate = async (reservationPayload) => {

    // 1. Determinar si es modificación o creación
    const isModification = !!reservationPayload.id;
    const reservationId = reservationPayload.id;

    // ... (Token logic remains the same) ...
    const account = msalInstance.getActiveAccount();
    if (!account) return; 

    let tokenResponse; 
    try {
        tokenResponse = await msalInstance.acquireTokenSilent({
            ...loginRequest,
            account: account
        });
    } catch (error) {
        msalInstance.loginRedirect(loginRequest); 
        return; 
    }
    const token = tokenResponse.accessToken;

    // 2. LLAMAR A MICROSOFT GRAPH (POST o PATCH)
    const url = isModification 
        ? `https://graph.microsoft.com/v1.0/me/events/${reservationId}` 
        : 'https://graph.microsoft.com/v1.0/me/events';

    const method = isModification ? 'PATCH' : 'POST'; // <-- Usa PATCH si hay ID

    const event = {
        subject: reservationPayload.title,
        start: { dateTime: reservationPayload.startTime, timeZone: "America/Bogota" },
        end: { dateTime: reservationPayload.endTime, timeZone: "America/Bogota" },
        attendees: [{
            emailAddress: { address: reservationPayload.roomEmail, name: reservationPayload.room.nombre },
            type: "resource"
        }],
        // Location is needed for PATCH, but Graph often ignores it for rooms
    };

    const response = await fetch(url, {
        method: method,
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
        body: JSON.stringify(event)
    });

    if (!response.ok) {
        const errorData = await response.json();
        console.error("Error Graph API:", errorData);
        throw new Error(`Error al ${isModification ? 'modificar' : 'guardar'}: ${errorData.error.message}`);
    }

    // La respuesta de PATCH o POST es el objeto de evento actualizado
    const apiResponse = await response.json(); 

    // 3. ACTUALIZAR ESTADO LOCAL
    setReservations(prev => {
        const baseReservation = { 
            ...reservationPayload, 
            id: apiResponse.id,
            startTime: apiResponse.start.dateTime,
            endTime: apiResponse.end.dateTime
        };
        // Nos aseguramos de que el campo 'room' exista antes de revivir fechas
        if (baseReservation.room) {
            baseReservation.room.nombre = reservationPayload.room.nombre; 
        }

        const newReservation = reviveDates([baseReservation])[0];

        if (isModification) {
            // Modificación: Buscar y reemplazar el objeto existente
            const others = prev.filter(x => x.id !== reservationId); // Usamos reservationId original
            return [newReservation, ...others];
        } else {
            // Creación: Agregar al principio
            return [newReservation, ...prev];
        }
    });
};

// Dentro de ReservationsProvider...

// -------------------------------------------------------------
// FUNCIÓN ASÍNCRONA DE ELIMINACIÓN (DELETE)
// -------------------------------------------------------------
const removeById = async (reservationId) => {
    const account = msalInstance.getActiveAccount();
    if (!account) throw new Error("Debe iniciar sesión para eliminar una reserva.");

    // Manejo de token
    let tokenResponse;
    try {
        tokenResponse = await msalInstance.acquireTokenSilent({
            ...loginRequest,
            account: account
        });
    } catch (error) {
        msalInstance.loginRedirect(loginRequest);
        return;
    }
    const token = tokenResponse.accessToken;

    // Resto de la lógica DELETE
    const response = await fetch(`https://graph.microsoft.com/v1.0/me/events/${reservationId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
    });

    if (!response.ok) {
        const errorText = await response.text();
        console.error("Error Graph API en DELETE:", errorText);
        throw new Error(`Error al eliminar: ${response.statusText}`);
    }

    // Si el DELETE fue exitoso (204 No Content), actualizamos el estado local
    setReservations(prev => prev.filter(r => r.id !== reservationId));
};

    // -------------------------------------------------------------

    const value = useMemo(() => ({ reservations, addOrUpdate, removeById }), [reservations]);

    return <ReservationsCtx.Provider value={value}>{children}</ReservationsCtx.Provider>;
};

export const useReservations = () => {
    const ctx = useContext(ReservationsCtx);
    if (!ctx) throw new Error("useReservations debe usarse dentro de <ReservationsProvider>");
    return ctx;
};
