import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import Header from './components/Header';
import CreateReservation from './pages/CreateReservation';
import MyReservations from './pages/MyReservations';
import AdminPanel from './pages/AdminPanel';
import { initialReservations } from './utils/reservations';

const AppContent = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [reservations, setReservations] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [reservationToEdit, setReservationToEdit] = useState(null); // <-- nuevo estado para editar
  const userName = 'Usuario Actual';

  useEffect(() => {
    setReservations(initialReservations);
  }, []);

  const handleAddReservation = (newReservation, action = 'add') => {
    if (action === 'edit') {
      setReservations(prev =>
        prev.map(r => (r.id === newReservation.id ? newReservation : r))
      );
      setReservationToEdit(null); // Limpiar edición después de guardar
    } else {
      setReservations(prev => [newReservation, ...prev]);
    }
  };

  const handleEditReservation = (reservation) => {
    // Guardar la reserva a editar en el estado
    setReservationToEdit(reservation);
    navigate('/'); // Redirige a CreateReservation
  };

  const handleDeleteReservation = (id) => {
    if (window.confirm('¿Estás seguro de eliminar esta reserva?')) {
      setReservations(prev => prev.filter(r => r.id !== id));
    }
  };

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div className="flex flex-col h-screen">
      <Header currentPath={location.pathname} />
      <Routes>
        <Route
          path="/"
          element={
            <CreateReservation
              reservations={reservations}
              onAddReservation={handleAddReservation}
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              reservationToEdit={reservationToEdit} // <-- pasar la reserva a editar
            />
          }
        />
        <Route
          path="/my-reservations"
          element={
            <MyReservations
              reservations={reservations}
              onAddReservation={handleAddReservation}
              onEdit={handleEditReservation}
              onDelete={handleDeleteReservation}
              userName={userName}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={location.pathname}
            />
          }
        />
        <Route
          path="/admin"
          element={
            <AdminPanel
              reservations={reservations}
              onEdit={handleEditReservation}
              onDelete={handleDeleteReservation}
              sidebarOpen={sidebarOpen}
              toggleSidebar={toggleSidebar}
              currentPath={location.pathname}
            />
          }
        />
      </Routes>
    </div>
  );
};

const App = () => (
  <Router>
    <AppContent />
  </Router>
);

export default App;
