import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css'; // O tus estilos
// 1. IMPORTA TU INSTANCIA MSAL
import { msalInstance } from './msalConfig'; 

const root = ReactDOM.createRoot(document.getElementById('root'));

// 2. INICIALIZA Y REVISA SI VIENES DE MICROSOFT
msalInstance.initialize().then(() => {
  // El "Recepcionista": Revisa si hay un código #code= en la URL
  msalInstance.handleRedirectPromise().then((response) => {

    // Si response existe, es que el login fue exitoso.
    if (response) { 
        console.log("¡Login exitoso! Usuario:", response.account);
        msalInstance.setActiveAccount(response.account); // Marca al usuario como activo
    }

    // 3. SOLO DESPUÉS DE REVISAR, PINTAMOS LA APP
    root.render(
      <React.StrictMode>
        <App />
      </React.StrictMode>
    );

  }).catch(error => console.log("Error en redirect:", error));
});
