// src/components/RoomsManager.jsx
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Plus, Trash2, Edit2, Save, X, Mail, MapPin } from 'lucide-react';
import { useReservations } from '../context/ReservationsContext';

export default function RoomsManager() {
  const { rooms, addOrUpdateRoom, deleteRoom } = useReservations();
  const [editingId, setEditingId] = useState(null);

  // Estado para el formulario (nuevo o edición)
  const [formData, setFormData] = useState({ id: '', nombre: '', email: '' });

  const handleEdit = (room) => {
    setEditingId(room.id);
    setFormData(room);
  };

  const handleCancel = () => {
    setEditingId(null);
    setFormData({ id: '', nombre: '', email: '' });
  };

  const handleSave = () => {
    if (!formData.nombre.trim()) return; // Validación básica
    addOrUpdateRoom(formData);
    handleCancel();
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-gray-800 dark:text-gray-100">
          Listado de Salas
        </h3>
        {/* Botón para iniciar creación si no estamos editando */}
        {!editingId && (
          <button
            onClick={() => { setEditingId('NEW'); setFormData({ id: '', nombre: '', email: '' }); }}
            className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
          >
            <Plus className="w-4 h-4" /> Nueva Sala
          </button>
        )}
      </div>

      {/* Formulario de Edición / Creación */}
      {editingId && (
        <motion.div 
          initial={{ opacity: 0, y: -10 }} animate={{ opacity: 1, y: 0 }}
          className="p-4 border border-blue-200 dark:border-blue-500/30 bg-blue-50/50 dark:bg-blue-500/10 rounded-2xl space-y-4"
        >
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-xs font-semibold uppercase text-gray-500">Nombre de la Sala</label>
              <div className="flex items-center gap-2 bg-white dark:bg-[#0F1318] border border-gray-200 dark:border-[#232A36] rounded-xl px-3 h-10 mt-1">
                <MapPin className="w-4 h-4 text-gray-400" />
                <input 
                  value={formData.nombre}
                  onChange={e => setFormData({...formData, nombre: e.target.value})}
                  placeholder="Ej: Sala de Juntas"
                  className="bg-transparent outline-none w-full text-sm"
                />
              </div>
            </div>
            <div>
              <label className="text-xs font-semibold uppercase text-gray-500">Email (Outlook)</label>
              <div className="flex items-center gap-2 bg-white dark:bg-[#0F1318] border border-gray-200 dark:border-[#232A36] rounded-xl px-3 h-10 mt-1">
                <Mail className="w-4 h-4 text-gray-400" />
                <input 
                  value={formData.email}
                  onChange={e => setFormData({...formData, email: e.target.value})}
                  placeholder="sala@dominio.com"
                  className="bg-transparent outline-none w-full text-sm"
                />
              </div>
            </div>
          </div>
          <div className="flex justify-end gap-2">
            <button onClick={handleCancel} className="px-4 py-2 text-gray-600 hover:bg-gray-200/50 rounded-lg text-sm">Cancelar</button>
            <button onClick={handleSave} className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-sm">
              <Save className="w-4 h-4" /> Guardar
            </button>
          </div>
        </motion.div>
      )}

      {/* Lista de Salas */}
      <div className="grid gap-3">
        {rooms.map(room => (
          <motion.div 
            key={room.id}
            layout
            className="group flex items-center justify-between p-4 bg-white dark:bg-[#151A20] border border-gray-100 dark:border-[#232A36] rounded-2xl shadow-sm hover:shadow-md transition-all"
          >
            <div className="flex items-center gap-4">
              <div className="w-10 h-10 rounded-full bg-indigo-50 dark:bg-indigo-500/10 flex items-center justify-center text-indigo-600 dark:text-indigo-400 font-bold">
                {room.nombre.charAt(0)}
              </div>
              <div>
                <h4 className="font-medium text-gray-900 dark:text-gray-100">{room.nombre}</h4>
                <p className="text-xs text-gray-500 dark:text-gray-400">{room.email || 'Sin correo asignado'}</p>
              </div>
            </div>

            <div className="flex gap-2 opacity-100 md:opacity-0 group-hover:opacity-100 transition-opacity">
              <button 
                onClick={() => handleEdit(room)}
                className="p-2 text-gray-500 hover:text-blue-600 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-lg transition"
              >
                <Edit2 className="w-4 h-4" />
              </button>
              <button 
                onClick={() => { if(window.confirm('¿Borrar sala?')) deleteRoom(room.id) }}
                className="p-2 text-gray-500 hover:text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-lg transition"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
