// src/components/ReservationForm.jsx
import React, { useMemo, useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { CalendarDays, Clock, Users, Type, MapPin, Save, X, Monitor, Projector, PenTool, Wifi, Video, Snowflake } from 'lucide-react';
import DatePickerField from '../components/DatePickerField';
import ConfirmationModal from './ui/ConfirmationModal';
import TimePickerField from './TimePickerField';
import { useReservations } from '../context/ReservationsContext'; 

// Mapa de iconos para renderizar (mismo que en RoomsManager)
const FEATURE_ICONS = {
    'TV / Monitor': Monitor,
    'Video Conferencia': Video,
    'Tablero': PenTool,
    'Proyector': Projector,
    'Aire Acondicionado': Snowflake,
    'WiFi Dedicado': Wifi,
};

const formatTime = (date) => date.toLocaleTimeString('en-US', {
    hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'America/Bogota'
});

// ‚úÖ FUNCI√ìN AUXILIAR AJUSTADA: Acepta prefillData
const getInitialValues = (reservationToEdit, userName, roomsList, prefillData) => {
    const safeRooms = roomsList || [];
    const defaultRoomId = safeRooms.length > 0 ? safeRooms[0].id : '';

    // PRIORITY 1: EDITANDO UNA RESERVA EXISTENTE
    if (reservationToEdit) {
        const start = new Date(reservationToEdit.startTime);
        const end = new Date(reservationToEdit.endTime);

        return {
            id: reservationToEdit.id,
            title: reservationToEdit.title || '',
            roomId: reservationToEdit.room?.id || defaultRoomId,
            date: start.toISOString().slice(0, 10),
            start: formatTime(start),
            end: formatTime(end),
            description: reservationToEdit.description || '',
            attendees: (Array.isArray(reservationToEdit.attendees) ? reservationToEdit.attendees.join(', ') : reservationToEdit.attendees) || '',
            location: reservationToEdit.location || '',
            user: reservationToEdit.user || userName || '',
        };
    }

    // ‚úÖ PRIORITY 2: DATOS PRE-LLENADOS DESDE EL CALENDARIO (Clic en espacio vac√≠o)
    if (prefillData) {
        const end = new Date(prefillData.endTime); // Fecha de fin calculada en CreateReservation.js
        return {
            id: undefined, 
            title: '', 
            roomId: prefillData.roomId || defaultRoomId, // Sala seleccionada
            date: prefillData.date,     // Fecha clickeada
            start: prefillData.start,   // Hora clickeada
            end: formatTime(end),       // Hora de fin (1 hora despu√©s)
            description: '', attendees: '', location: '', user: userName || '',
        };
    }

    // PRIORITY 3: VALORES POR DEFECTO
    return {
        id: undefined, title: '', roomId: defaultRoomId, date: '', 
        start: '09:00', end: '10:00', description: '', attendees: '', location: '', user: userName || '',
    };
};

export default function ReservationForm({ 
    userName, 
    reservationToEdit, 
    onUpsert, 
    onCancel, 
    prefillData // ‚úÖ ACEPTAMOS EL NUEVO PROP
}) {
  const { rooms, checkAvailability } = useReservations(); 

  // ‚úÖ INICIALIZACI√ìN: Ahora pasamos prefillData
  const [values, setValues] = useState(() => getInitialValues(reservationToEdit, userName, rooms, prefillData));

  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState({});
  const [confirmOpen, setConfirmOpen] = useState(false);
  const [confirmData, setConfirmData] = useState(null);
  const [busySlots, setBusySlots] = useState([]);
  const [checkingAvailability, setCheckingAvailability] = useState(false);

  // EFECTO: Consultar disponibilidad
  useEffect(() => {
    if (!values.date || !values.roomId) {
        setBusySlots([]);
        return;
    }
    const fetchBusySlots = async () => {
        setCheckingAvailability(true);
        const currentRoom = rooms.find(r => r.id === values.roomId);
        if (currentRoom?.email) {
            const slots = await checkAvailability(currentRoom.email, values.date);
            setBusySlots(slots);
        } else {
            setBusySlots([]);
        }
        setCheckingAvailability(false);
    };
    const timer = setTimeout(fetchBusySlots, 500);
    return () => clearTimeout(timer);
  }, [values.date, values.roomId, rooms, checkAvailability]);

  // BUSCAR LA SALA SELECCIONADA (Para mostrar sus datos)
  const selectedRoomData = useMemo(() => {
      if (!rooms) return null;
      return rooms.find(r => r.id === values.roomId);
  }, [rooms, values.roomId]);

  const handle = (k) => (e) => setValues(v => ({ ...v, [k]: e.target.value }));

  const toMinutes = (timeStr) => {
      if (!timeStr) return 0;
      const [h, m] = timeStr.split(':').map(Number);
      return h * 60 + m;
  };

  const validate = () => {
    const e = {};
    if (!values.title.trim()) e.title = 'El t√≠tulo es obligatorio.';
    if (!values.date) e.date = 'Selecciona una fecha.';
    if (!values.start || !values.end) e.time = 'Indica hora de inicio y fin.';

    if (values.start && values.end) {
        const startM = toMinutes(values.start);
        const endM = toMinutes(values.end);

        if (endM <= startM) {
            e.time = 'La hora de fin debe ser mayor a la de inicio.';
        } else {
            const hasConflict = busySlots.some(slot => {
                const slotStart = new Date(slot.start);
                const slotEnd = new Date(slot.end);
                const sSlot = slotStart.getHours() * 60 + slotStart.getMinutes();
                const eSlot = slotEnd.getHours() * 60 + slotEnd.getMinutes();
                return (startM < eSlot && endM > sSlot);
            });
            if (hasConflict) e.time = '‚ö†Ô∏è El horario coincide con otra reserva existente.';
        }
    }
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const persist = async (evt) => {
    evt.preventDefault();
    if (!validate()) return;
    setSaving(true);
    try {
      const startTime = new Date(`${values.date}T${values.start}:00`);
      const endTime = new Date(`${values.date}T${values.end}:00`);
      const selectedRoom = selectedRoomData || (rooms && rooms.length > 0 ? rooms[0] : { nombre: 'Sin sala', email: '' });

      const payload = {
        id: values.id,
        title: values.title.trim(),
        room: selectedRoom,
        roomEmail: selectedRoom.email,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        description: values.description.trim(),
        attendees: values.attendees ? values.attendees.split(',').map(s => s.trim()).filter(Boolean) : [],
        location: values.location.trim(),
        user: values.user,
      };
      await onUpsert?.(payload);

      setConfirmData ({
        title: payload.title,
        roomName: payload.room?.nombre || 'Sala',
        start: payload.startTime,
        end: payload.endTime,
      });
      setConfirmOpen(true);
    } finally {
      setSaving(false);
    }
  };

  return (
    <motion.form
      onSubmit={persist}
      initial={{ opacity: 0, y: 6 }}
      animate={{ opacity: 1, y: 0 }}
      className="rounded-2xl p-5 md:p-6 lg:p-7 space-y-6 bg-white dark:bg-[#171B22] border border-gray-200 dark:border-[#232A36] shadow-sm"
    >
      <div className="flex items-start justify-between gap-4">
        <div>
          <h3 className="text-xl md:text-2xl font-semibold tracking-tight text-gray-900 dark:text-white">
            {values.id ? 'Editar reserva' : 'Nueva reserva'}
          </h3>
          <p className="mt-1.5 text-sm text-gray-600 dark:text-gray-400">Completa los campos y guarda los cambios.</p>
        </div>
        {onCancel && (
          <button type="button" onClick={onCancel} className="h-10 px-3 inline-flex items-center gap-2 rounded-xl bg-gray-50 dark:bg-[#0F1318] border border-gray-200 dark:border-[#2A3342] text-gray-800 dark:text-gray-100 hover:bg-gray-100 dark:hover:bg-[#141922] transition">
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6">
        <div className="md:col-span-2">
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Type className="w-4 h-4 opacity-80" /> T√≠tulo*
          </label>
          <input
            value={values.title}
            onChange={handle('title')}
            className={`mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border ${errors.title ? 'border-red-500' : 'border-gray-300 dark:border-[#2A3342]'} text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 ${errors.title ? 'focus:ring-red-500/40' : 'focus:ring-blue-500/30'}`}
            placeholder="Breve nombre de la reuni√≥n"
          />
          {errors.title && <p className="mt-1 text-xs text-red-500">{errors.title}</p>}
        </div>

        {/* SELECTOR DE SALA + TARJETA DE INFO */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <MapPin className="w-4 h-4 opacity-80" /> Sala
          </label>
          <select
            value={values.roomId}
            onChange={handle('roomId')}
            className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30"
          >
            {rooms && rooms.map(r => (
              <option key={r.id} value={r.id}>{r.nombre}</option>
            ))}
          </select>

          {/* üÜï TARJETA DE DETALLES DE LA SALA */}
          {selectedRoomData && (
            <div className="mt-3 p-3 bg-blue-50 dark:bg-blue-900/10 border border-blue-100 dark:border-blue-800/30 rounded-xl flex flex-wrap gap-3 items-center">
                {/* Capacidad */}
                <div className="flex items-center gap-1.5 text-xs font-medium text-blue-700 dark:text-blue-300">
                    <Users className="w-4 h-4" />
                    {selectedRoomData.capacidad || 4} Personas
                </div>
                {/* Recursos (Iconos) */}
                {selectedRoomData.recursos && selectedRoomData.recursos.length > 0 && (
                    <div className="flex items-center gap-2 border-l border-blue-200 dark:border-blue-700 pl-3 ml-1">
                        {selectedRoomData.recursos.map(rec => {
                            const Icon = FEATURE_ICONS[rec];
                            if(!Icon) return null;
                            return <Icon key={rec} className="w-4 h-4 text-blue-600/70 dark:text-blue-400" title={rec} />
                        })}
                    </div>
                )}
            </div>
          )}
        </div>

        <div>
           <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
              <CalendarDays className="w-4 h-4 opacity-80" /> Fecha*
           </label>
           <DatePickerField id="date" value={values.date} onChange={(v) => setValues(s => ({ ...s, date: v }))} required />
           {errors.date && <p className="mt-1 text-xs text-red-500">{errors.date}</p>}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6 pt-2">
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Clock className="w-4 h-4 opacity-80" /> Hora inicio*
          </label>
          <TimePickerField value={values.start} onChange={(val) => setValues(v => ({...v, start: val}))} use12h={false} minuteStep={30} />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Clock className="w-4 h-4 opacity-80" /> Hora fin*
          </label>
          <TimePickerField value={values.end} onChange={(val) => setValues(v => ({...v, end: val}))} use12h={false} minuteStep={30} />
          {errors.time && <p className="mt-1 text-xs text-red-500">{errors.time}</p>}
        </div>

        <div className="md:col-span-2 mt-2">
           {checkingAvailability ? (
               <p className="text-xs text-blue-500 animate-pulse">Verificando agenda de la sala...</p>
           ) : busySlots.length > 0 ? (
               <div className="bg-orange-50 dark:bg-orange-900/10 border border-orange-100 dark:border-orange-900/20 rounded-xl p-3">
                   <p className="text-xs font-semibold text-orange-700 dark:text-orange-400 mb-2 flex items-center gap-1">
                      <span className="w-2 h-2 rounded-full bg-orange-500"></span>
                      Horarios ya reservados para este d√≠a:
                   </p>
                   <div className="flex flex-wrap gap-2">
                      {busySlots.map((slot, i) => {
                          const s = new Date(slot.start).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit', hour12: true});
                          const e = new Date(slot.end).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit', hour12: true});
                          return (
                              <span key={i} className="px-2 py-1 bg-white dark:bg-orange-950/30 border border-orange-200 dark:border-orange-800/30 rounded-md text-xs text-orange-600 dark:text-orange-300 font-mono">
                                  {s} - {e}
                              </span>
                          )
                      })}
                   </div>
               </div>
           ) : values.date && values.roomId ? (
               <p className="text-xs text-green-600 dark:text-green-400 flex items-center gap-1">
                  <span className="w-2 h-2 rounded-full bg-green-500"></span>
                  Agenda libre para este d√≠a
               </p>
           ) : null}
        </div>
      </div>

      <div className="grid grid-cols-1 gap-5 md:gap-6 pt-2">
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">Descripci√≥n</label>
          <textarea rows={3} value={values.description} onChange={handle('description')} className="mt-1.5 w-full rounded-xl p-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="Contexto breve..." />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <Users className="w-4 h-4 opacity-80" /> Invitados
          </label>
          <input value={values.attendees} onChange={handle('attendees')} className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="ana@ejemplo.com, juan@ejemplo.com" />
        </div>
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5">
            <MapPin className="w-4 h-4 opacity-80" /> Ubicaci√≥n (opcional)
          </label>
          <input value={values.location} onChange={handle('location')} className="mt-1.5 w-full h-11 rounded-xl px-3.5 bg-white dark:bg-[#0F1318] border border-gray-300 dark:border-[#2A3342] text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500/30" placeholder="Enlace o lugar f√≠sico" />
        </div>
      </div>

      <div className="pt-3 mt-1 flex items-center justify-end gap-2">
        {onCancel && (
          <button type="button" onClick={onCancel} className="h-10 px-3 inline-flex items-center gap-2 rounded-xl bg-gray-50 dark:bg-[#0F1318] border border-gray-200 dark:border-[#2A3342] text-gray-800 dark:text-gray-100 hover:bg-gray-100 dark:hover:bg-[#141922]">
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}

        <ConfirmationModal
          open={confirmOpen}
          onClose={() => setConfirmOpen(false)}
          data={confirmData || {}}
          onCTA={{ primary: () => { setConfirmOpen(false); window.location.href = '/my-reservations'; } }}
        />

        <motion.button type="submit" disabled={saving} whileTap={{ scale: saving ? 1 : 1 }} className={`h-10 px-4 inline-flex items-center gap-2 rounded-xl text-white font-semibold shadow ${saving ? 'bg-blue-500/70 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-500'} ring-1 ring-blue-400/30`}>
          <Save className="w-4 h-4" />
          {saving ? 'Guardando‚Ä¶' : values.id ? 'Guardar cambios' : 'Crear reserva'}
        </motion.button>
      </div>
    </motion.form>
  );
}
