// src/components/ReservationForm.jsx
import React, { useEffect, useMemo, useState } from 'react';
import { motion } from 'framer-motion';
import { CalendarDays, Clock, Users, Type, MapPin, Save, X } from 'lucide-react';
import DatePickerField from '../components/DatePickerField';
import ConfirmationModal from './ui/ConfirmationModal';
import { title } from 'framer-motion/client';
import TimePickerField from './TimePickerField';
import { cacheAdd } from '../utils/reservationsCache';

const ROOMS = [
  { id: '1', nombre: 'Círculo de Sabios I', email: 'circulo.sabios1@fycotelecom.com' },
  { id: '2', nombre: 'Círculo de Sabios II', email: 'circulo.sabios2@fycotelecom.com' },
  { id: '3', nombre: 'Círculo de Sabios III', email: 'circulo.sabios3@fycotelecom.com' },
  { id: '4', nombre: 'Consejo de la Tribu', email: 'consejo.tribu@fycotelecom.com' },
  { id: '5', nombre: 'Plaza de Conexiones', email: 'plaza.conexiones@fycotelecom.com' },
];

// ✅ FUNCIÓN AUXILIAR PARA CALCULAR EL ESTADO INICIAL
const getInitialValues = (reservationToEdit, userName, ROOMS) => {
    // Modo Creación (default)
    if (!reservationToEdit) {
        return {
            id: undefined, title: '', roomId: ROOMS[0].id, date: '', start: '09:00',
            end: '10:00', description: '', attendees: '', location: '', user: userName || '',
        };
    }

    // Modo Edición: Extraemos todos los datos (CRÍTICO: el ID y el formato de hora)
    const start = new Date(reservationToEdit.startTime);
    const end = new Date(reservationToEdit.endTime);

    const formatTime = (date) => date.toLocaleTimeString('en-US', {
        hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'America/Bogota'
    });

    return {
        id: reservationToEdit.id,
        title: reservationToEdit.title || '',
        roomId: reservationToEdit.room?.id || ROOMS[0].id,
        date: start.toISOString().slice(0, 10),
        start: formatTime(start),
        end: formatTime(end),
        description: reservationToEdit.description || '',
        // Si el campo attendees es un array, lo convertimos a string separado por comas
        attendees: (Array.isArray(reservationToEdit.attendees) ? reservationToEdit.attendees.join(', ') : reservationToEdit.attendees) || '',
        location: reservationToEdit.location || '',
        user: reservationToEdit.user || userName || '',
    };
};

export default function ReservationForm({
  userName,
  reservationToEdit,
  allReservations = [],
  onUpsert,
  onCancel,
}) {

  // 1. ✅ CÓDIGO FINAL: Inicializa el estado usando la función auxiliar
  const [values, setValues] = useState(() => 
    getInitialValues(reservationToEdit, userName, ROOMS)
  );

  // 2. ❌ ELIMINAMOS EL useEffect ANTIGUO, ya que el estado se inicializa arriba
  // y solo se necesita aquí para los hooks que vienen después.
  // ... (No hay código aquí donde antes estaba el useEffect) ...


  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState({});
  const [confirmOpen, setConfirmOpen] = useState(false); //Modal de confirmación
  const[confirmData, setConfirmData] =useState(null); // {title, roomName, start, end}


  const room = useMemo(() => ROOMS.find(r => r.id === values.roomId), [values.roomId]);
  const handle = (k) => (e) => setValues(v => ({ ...v, [k]: e.target.value }));

  const validate = () => {
    const e = {};
    if (!values.title.trim()) e.title = 'El título es obligatorio.';
    if (!values.date) e.date = 'Selecciona una fecha.';
    if (!values.start || !values.end) e.time = 'Indica hora de inicio y fin.';
    if (values.date && values.start && values.end) {
      const s = new Date(`${values.date}T${values.start}:00`);
      const f = new Date(`${values.date}T${values.end}:00`);
      if (f <= s) e.time = 'La hora de fin debe ser mayor a la de inicio.';
    }
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const persist = async (evt) => {
    evt.preventDefault();
    if (!validate()) return;
    setSaving(true);
    try {
      const startTime = new Date(`${values.date}T${values.start}:00`);
      const endTime = new Date(`${values.date}T${values.end}:00`);
      const payload = {
        id: values.id, // ✅ EL ID AHORA ESTÁ GARANTIZADO AQUÍ
        title: values.title.trim(),
        room,
        roomEmail: room.email,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        description: values.description.trim(),
        attendees: values.attendees
          ? values.attendees.split(',').map(s => s.trim()).filter(Boolean)
          : [],
        location: values.location.trim(),
        user: values.user,
      };
      await onUpsert?.(payload);
      // ← abrir modal de confirmación con datos clave
      setConfirmData ({
        title: payload.title,
        roomName: payload.room?.nombre || 'sala',
        start: payload.startTime,
        end: payload.endTime,
      });
      setConfirmOpen(true);
    } finally {
      setSaving(false);
    }
  };

  return (
    <motion.form
      onSubmit={persist}
      initial={{ opacity: 0, y: 6 }}
      animate={{ opacity: 1, y: 0 }}
      className="
        rounded-2xl p-5 md:p-6 lg:p-7 space-y-6
        bg-white dark:bg-[#171B22]
        border border-gray-200 dark:border-[#232A36]
        shadow-sm
      "
    >
      {/* Encabezado */}
      <div className="flex items-start justify-between gap-4">
        <div>
          <h3 className="text-xl md:text-2xl font-semibold tracking-tight text-gray-900 dark:text-white">
            {/* ✅ El título ahora depende del ID cargado dinámicamente */}
            {values.id ? 'Editar reserva' : 'Nueva reserva'}
          </h3>
          <p className="mt-1.5 text-sm text-gray-600 dark:text-gray-400">
            Completa los campos y guarda los cambios.
          </p>
        </div>
        {onCancel && (
          <button
            type="button"
            onClick={onCancel}
            className="h-10 px-3 inline-flex items-center gap-2 rounded-xl
                       bg-gray-50 dark:bg-[#0F1318]
                       border border-gray-200 dark:border-[#2A3342]
                       text-gray-800 dark:text-gray-100
                       hover:bg-gray-100 dark:hover:bg-[#141922]
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30 dark:focus:ring-blue-400/25"
          >
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}
      </div>

      {/* Sección: Detalles */}
      <div className="pt-1">
        <div className="flex items-center gap-3">
          <h4 className="text-[11px] font-semibold tracking-wider uppercase text-gray-500 dark:text-gray-300">
            Detalles
          </h4>
          <div className="h-px flex-1 bg-gray-200/70 dark:bg-white/10" />
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6">
        {/* Título */}
        <div className="md:col-span-2">
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <Type className="w-4 h-4 opacity-80" />
            Título*
          </label>
          <input
            value={values.title}
            onChange={handle('title')}
            className={`
              mt-1.5 w-full h-11 rounded-xl px-3.5
              bg-white dark:bg-[#0F1318]
              border ${errors.title ? 'border-red-500' : 'border-gray-300 dark:border-[#2A3342]'}
              text-gray-900 dark:text-gray-100
              placeholder:text-gray-400 dark:placeholder:text-gray-500
              focus:outline-none focus:ring-2 ${errors.title ? 'focus:ring-red-500/40' : 'focus:ring-blue-500/30'}
            `}
            placeholder="Breve nombre de la reunión"
          />
          {errors.title && <p className="mt-1 text-xs text-red-500">{errors.title}</p>}
        </div>

        {/* Sala */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <MapPin className="w-4 h-4 opacity-80" />
            Sala
          </label>
          <select
            value={values.roomId}
            onChange={handle('roomId')}
            className="mt-1.5 w-full h-11 rounded-xl px-3.5
                       bg-white dark:bg-[#0F1318]
                       border border-gray-300 dark:border-[#2A3342]
                       text-gray-900 dark:text-gray-100
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30"
          >
            {ROOMS.map(r => (
              <option key={r.id} value={r.id}>{r.nombre}</option>
            ))}
          </select>
        </div>

        {/* Fecha */}
          <div>
            <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
              <CalendarDays className="w-4 h-4 opacity-80" />
              Fecha*
            </label>
            <DatePickerField
              id="date"
              value={values.date}
              onChange={(v) => setValues(s => ({ ...s, date: v }))}
              required
            />
            {errors.date && <p className="mt-1 text-xs text-red-500">{errors.date}</p>}
          </div>
      </div>

      {/* Sección: Horario */}
      <div className="pt-2">
        <div className="flex items-center gap-3">
          <h4 className="text-[11px] font-semibold tracking-wider uppercase text-gray-500 dark:text-gray-300">
            Horario
          </h4>
          <div className="h-px flex-1 bg-gray-200/70 dark:bg-white/10" />
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-5 md:gap-6">
        {/* Hora inicio */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <Clock className="w-4 h-4 opacity-80" />
            Hora inicio*
          </label>
          <TimePickerField
          value={values.start}
          onChange={(val) => setValues(v => ({...v, start: val}))}
          use12h={false}
          minuteStep={30}
          />
        </div>

        {/* Hora fin */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <Clock className="w-4 h-4 opacity-80" />
            Hora fin*
          </label>
          <TimePickerField
          value={values.end}
          onChange={(val) => setValues(v => ({...v, end: val}))}
          use12h={false}
          minuteStep={30}
          />
          {errors.time && <p className="mt-1 text-xs text-red-500">{errors.time}</p>}
        </div>
      </div>

      {/* Sección: Extras */}
      <div className="pt-2">
        <div className="flex items-center gap-3">
          <h4 className="text-[11px] font-semibold tracking-wider uppercase text-gray-500 dark:text-gray-300">
            Extras
          </h4>
          <div className="h-px flex-1 bg-gray-200/70 dark:bg-white/10" />
        </div>
      </div>

      <div className="grid grid-cols-1 gap-5 md:gap-6">
        {/* Descripción */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            Descripción
          </label>
          <textarea
            rows={3}
            value={values.description}
            onChange={handle('description')}
            className="mt-1.5 w-full rounded-xl p-3.5 leading-6
                       bg-white dark:bg-[#0F1318]
                       border border-gray-300 dark:border-[#2A3342]
                       text-gray-900 dark:text-gray-100
                       placeholder:text-gray-400 dark:placeholder:text-gray-500
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30"
            placeholder="Contexto breve de la reunión"
          />
        </div>

        {/* Invitados */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <Users className="w-4 h-4 opacity-80" />
            Invitados (emails separados por coma)
          </label>
          <input
            value={values.attendees}
            onChange={handle('attendees')}
            className="mt-1.5 w-full h-11 rounded-xl px-3.5
                       bg-white dark:bg-[#0F1318]
                       border border-gray-300 dark:border-[#2A3342]
                       text-gray-900 dark:text-gray-100
                       placeholder:text-gray-400 dark:placeholder:text-gray-500
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30"
            placeholder="ana@ejemplo.com, juan@ejemplo.com"
          />
        </div>

        {/* Ubicación */}
        <div>
          <label className="flex items-center gap-2 text-[13px] font-medium text-gray-700 dark:text-gray-300 mb-1.5 leading-6">
            <MapPin className="w-4 h-4 opacity-80" />
            Ubicación (opcional)
          </label>
          <input
            value={values.location}
            onChange={handle('location')}
            className="mt-1.5 w-full h-11 rounded-xl px-3.5
                       bg-white dark:bg-[#0F1318]
                       border border-gray-300 dark:border-[#2A3342]
                       text-gray-900 dark:text-gray-100
                       placeholder:text-gray-400 dark:placeholder:text-gray-500
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30"
            placeholder="Sala física o enlace"
          />
        </div>
      </div>

      {/* Acciones */}
      <div className="pt-3 mt-1 flex items-center justify-end gap-2">
        {onCancel && (
          <button
            type="button"
            onClick={onCancel}
            className="h-10 px-3 inline-flex items-center gap-2 rounded-xl
                       bg-gray-50 dark:bg-[#0F1318]
                       border border-gray-300 dark:border-[#2A3342]
                       text-gray-800 dark:text-gray-100
                       hover:bg-gray-100 dark:hover:bg-[#141922]
                       focus:outline-none focus:ring-2 focus:ring-blue-500/30 dark:focus:ring-blue-400/25"
          >
            <X className="w-4 h-4" /> Cancelar
          </button>
        )}
/</div>
      {/* Modal fuera del flujo, no altera tu layout */}
            <ConfirmationModal
            open={confirmOpen}
            onClose={() => setConfirmOpen(false)}
            data={confirmData || {}}
            onCTA={{
            primary: () => {
            setConfirmOpen(false);
            // si tienes router, reemplaza por navigate('/my-reservations');
            window.location.href = '/my-reservations';
            }
            }}
            />

      {/* Acciones */}
      <div className="pt-3 mt-1 flex items-center justify-end gap-2">
        {/* ... */}
        <motion.button
          type="submit"
          disabled={saving}
          whileTap={{ scale: saving ? 1 : 0.98 }}
          className={`h-10 px-4 inline-flex items-center gap-2 rounded-xl text-white font-semibold shadow
            ${saving ? 'bg-blue-500/70 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-500'}
            ring-1 ring-blue-400/30 focus:outline-none focus:ring-2 focus:ring-blue-400/40`}
        >
          <Save className="w-4 h-4" />
          {saving ? 'Guardando…' : values.id ? 'Guardar cambios' : 'Crear reserva'}
        </motion.button>
      </div>
    </motion.form>
  );
}
