import { useEffect, useState } from 'react';
import { msalInstance, loginRequest } from '../msalConfig'; // MSAL
import { useReservations } from '../context/ReservationsContext'; // Contexto (para removeById)
import { useNavigate } from 'react-router-dom';

// --- FUNCIN DE UTILIDAD: EXTRACCIN ROBUSTA DEL NOMBRE DE LA SALA ---
// Debe estar definida aqu铆 para ser usada en el mapeo de fetchReservations()
const getRoomName = (reservation) => {
    // PRIORIDAD 1: Buscar el objeto 'room' que creamos localmente (para reservas nuevas)
    if (reservation.room?.nombre) {
        return reservation.room.nombre;
    }

    // PRIORIDAD 2: Buscar el recurso dentro del array de asistentes (para reservas antiguas de Outlook)
    const roomAttendee = reservation.attendees?.find(a => a.type === 'resource');

    if (roomAttendee) {
        return roomAttendee.emailAddress?.name || roomAttendee.emailAddress?.address;
    }

    // PRIORIDAD 3: Usar el display name como fallback
    if (reservation.location?.displayName && reservation.location.displayName !== "Sala no especificada") {
        return reservation.location.displayName;
    }

    return 'Sala no especificada';
};

// --- FUNCIN DE UTILIDAD: FORMATEO DE HORA ---
const hhmm = (d) => d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });


export default function MyReservations() {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const { removeById } = useReservations(); 
  const navigate = useNavigate ();

  // Funci贸n para manejar la eliminaci贸n y la UX (mostrar si falla)
  const handleDelete = async (reservationId) => {
      if (!window.confirm('驴Est谩s seguro de que quieres eliminar esta reserva de Outlook?')) {
          return;
      }
      try {
          await removeById(reservationId);
          // Actualizamos el estado local eliminando el item (ya lo hace el Contexto, pero forzamos el re-render)
          setReservations(prev => prev.filter(r => r.id !== reservationId)); 
      } catch (e) {
          setError(`No se pudo eliminar: ${e.message}`);
      }
  };

  useEffect(() => {
    let alive = true;

    async function fetchReservations() {
      setLoading(true);
      setError('');

      try {
        const account = msalInstance.getActiveAccount();
        if (!account) throw new Error('No hay sesi贸n activa. Por favor inicia sesi贸n arriba.');

        const tokenResponse = await msalInstance.acquireTokenSilent({
            ...loginRequest,
            account: account
        });
        const token = tokenResponse.accessToken;

        // 3. LLAMAR A MICROSOFT GRAPH (Incluye 'attendees' y 'location')
        const response = await fetch('https://graph.microsoft.com/v1.0/me/events?$select=subject,start,end,location,id,attendees&$top=50', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
            'Prefer': 'outlook.timezone="America/Bogota"'
          }
        });

        if (!response.ok) throw new Error(`Error Microsoft: ${response.status}`);

        const graphData = await response.json();

        // 4. ADAPTAR DATOS PARA TU LISTA (Usando la funci贸n getRoomName)
        const mapped = (graphData.value || []).map(evt => ({
            id: evt.id,
            title: evt.subject,
            startTime: evt.start.dateTime,
            endTime: evt.end.dateTime,
            // Aplicamos la l贸gica de extracci贸n al cargar los datos:
            room: { nombre: getRoomName(evt) }, 
            attendees: evt.attendees // Mantenemos el array de asistentes si fuera necesario
        }));

        if (alive) setReservations(mapped);

      } catch (e) {
        console.error(e);
        if (alive) setError(e.message || 'No se pudieron cargar las reservas');
      } finally {
        if (alive) setLoading(false);
      }
    }

    fetchReservations();
    return () => { alive = false; };
  }, []); 


  // --- RENDERIZADO ---
  return (
    <>
      {error && (<div className="mx-auto max-w-4xl mt-6 rounded-xl border border-red-200 bg-red-50 px-4 py-3 text-sm text-red-800">{error}</div>)}
      {loading && !error && (<div className="mx-auto max-w-4xl mt-6 text-sm text-gray-600">Cargando tus reservas de Outlook...</div>)}

      {!loading && !error && reservations.length > 0 && (
        <ul className="mx-auto max-w-4xl mt-4 space-y-3">
          {reservations.map((r) => {
            const start = new Date(r.startTime);
            const end = new Date(r.endTime);
            const dateStr = start.toLocaleDateString([], { day: '2-digit', month: 'short' });

            return (
              <li key={r.id} className="rounded-xl bg-white border border-gray-200 px-4 py-3">
                <div className="flex justify-between items-center">
                    <div className="font-semibold">{r.title || '(Sin t铆tulo)'}</div>

                    {/* BOTONES DE ACCIN */}
                    <div className='flex gap-2'>
                        <button
                          onClick={() => navigate(`/CreateReservation/${r.id}`)} //  AHORA NAVEGA
                          className='text-blue-600 hover:text-blue-500 text-sm'> Editar
                      </button>
                        <button
                            onClick={() => handleDelete(r.id)} 
                            className="text-red-600 hover:text-red-400 text-sm"> Eliminar
                        </button>
                    </div>
                </div>

                <div className="text-sm text-gray-600">
                  {/* El nombre de la sala se lee directamente de la data mapeada */}
                  {r.room?.nombre || 'Sala no especificada'} 路 {dateStr}, {hhmm(start)}{hhmm(end)}
                </div>
              </li>
            );
          })}
        </ul>
      )}
    </>
  );
}
