import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu } from 'lucide-react';
import Sidebar from '../components/Sidebar';
import ReservationForm from '../components/ReservationForm';
import OutlookSync from '../components/OutlookSync';
import { useReservations } from '../context/ReservationsContext';
import { useParams } from 'react-router-dom'; //Para leer el ID de la URL

const CreateReservation = ({
  userName,
  sidebarOpen,
  toggleSidebar,
  currentPath,
}) => {
  const { id } = useParams(); //ID de la URL (ej: 'XYZ123')
  const { reservations } = useReservations(); // 1. OBTENER LA LISTA Y LA FUNCIÓN DE GUARDADO DEL CONTEXTO

  const isEditing =!!id;
  const reservationToEdit = reservations.find(r => r.id === id); // 2. BUSCAR LA RESERVA A EDITAR

  if (isEditing && !reservationToEdit) {
    // Si estamos editando y aún no encontramos la reserva en la lista
    return (
      <div className="mx-auto max-w-4xl mt-10 p-6 text-center">
        Cargando datos de la reserva...
      </div>
    );
  }

return (
